INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 753B', 'Research Essay - Level 9', '', 'Prerequisite: An average of A– over four courses or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 754', 'English for Specific Purposes', 'Considers theory and practice in developing language courses to meet the specific academic or work-related needs of adult learners. In particular, it focuses on analysis of target communication, needs analysis, issues and methodologies in ESP and ESP research. Uses the example of English for Specific Purposes but is relevant to the teaching of other languages for specific purposes as well.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 756', 'Special Topic - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 757', 'Conducting Research in Applied Language Studies - Level 9', 'Explores advanced theoretical perspectives for research into second/foreign language learning, teaching, and use. Enables students to acquire an advanced understanding of selected research methods. Students work independently on a small-scale project, which involves the collection and analysis of data on second or foreign language learning, teaching or use.', 'Restriction: LANGTCHG 732, 745', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 760', 'Curriculum Design - Level 9', 'Advanced level study of theoretical principles and current issues in the second language curriculum. Includes historical examination of influential approaches and methods, with particular attention to the development and current position of communicative approaches. Examines issues associated with course design processes including assessment of needs, objectives setting, syllabus and materials selections, and course evaluation.', 'Restriction: LANGTCHG 741', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 761', 'Sociolinguistics - Level 9', 'Examines sociolinguistic concepts relevant to understanding influences on language use at the level of individuals, social groups and institutions. Enables students to acquire an advanced understanding of social and regional dialects, perceived differences in the market status of particular languages, the use of sociolects and ethnolects in the media and other public domains, and policies and practices concerning migrant and heritage language maintenance.', 'Restriction: LANGTCHG 749', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 762', 'Second Language Acquisition - Level 9', 'Advanced study of current theories of second language acquisition and research that have examined both instructed and naturalistic acquisition. Includes an independent study involving analysis of learner language and writing a standard research report and a critique of a pedagogical approach, drawing on contemporary research on how languages are learned.', 'Restriction: LANGTCHG 722, 723, 743', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 763', 'Discourse Analysis - Level 9', 'Examines major theoretical approaches to the analysis of discourse and implications for pedagogy. Students acquire specialised knowledge of approaches to analysis and frameworks that underpin research, and develop advanced skills in written and spoken text analysis and critical awareness of issues and debates in the field. Assignments involve substantial independent research that includes analysis and interpretation of data.', 'Restriction: LANGTCHG 744', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 764', 'Creativity: Research and Practice - Level 9', 'Theoretical perspectives, pedagogical practices and research projects related to the topic of creativity in language learning and teaching. Enables students to acquire an advanced understanding of the concept of ‘creativity’ with reference to language teaching and learning through exploratory research, practice and reflection.', 'Restriction: LANGTCHG 755', 'null', 'null');
INSERT INTO `arts` VALUES ('Language Teaching and Learning', 'LANGTCHG 765', 'Language Testing and Assessment', 'Advanced study of theoretical principles and current issues in language testing, assessment and evaluation, and their application to teaching practice and research. Examines issues associated with the creation of valid and reliable assessments, and the implications for course design processes, with particular attention to the testing, assessment and evaluation of communicative language proficiency.', 'Restriction: LANGTCHG 742', 'null', 'null');
