INSERT INTO `arts` VALUES ('Latin American Studies', 'LATINAM 200', 'Special Topic: Brazil in Global Cultural History', 'An introduction to Latin America’s largest country from Indigenous First Nations to European conquest and Afro-Brazilian resistance and settlement, to Brazil’s current rise as a cultural an economic global power. Through a multimedia and interdisciplinary approach, the course addresses Brazil’s growing influence on the world stage, placing its culture (music, cinema, literature, visual arts, sports), environment, economy and geopolitics in historical context.', 'Prerequisite: 15 points from SPANISH 103, 105, 200, 201, 277, 278, 319, 321, 377, 378, or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Latin American Studies', 'LATINAM 201', 'Latin American History and Culture Through Film', 'A journey through five moments of Latin American history and culture, from its creation as a region imagined through the gaze of colonialism, through the development of an independent, revolutionary Third Cinema, to the present when globalisation is critiqued from the periphery.', 'Prerequisite: 15 points from COMMS 100, FTVMS 100, 101, 112, MEDIA 101, SPANISH 103, 105, 200, 201, 277, 278, 319, 321, 377, 378, or 45 points in BGlobalSt courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Latin American Studies', 'LATINAM 202', 'Study Abroad (Latin America)', 'A minimum of four weeks of study at an overseas institution approved by the Spanish and Latin American Study Abroad Adviser.', 'Prerequisite: Programme Coordinator approval', 'null', 'null');
INSERT INTO `arts` VALUES ('Latin American Studies', 'LATINAM 210', 'Visual Cultures and Industries in Latin America', 'Follows transformations in Latin American visual cultures and culture industries since the 1930s, from the liberal to the neoliberal era. Throughout this history, who controls visual media production and how does this control relate to democracy? Have recent alliances between Indigenous groups and social movements to introduce new Media Laws successfully widened participation and challenged corporate media ownership?', 'Prerequisite: 45 points in BA courses or approval of Programme Coordinator', 'null', 'null');
INSERT INTO `arts` VALUES ('Latin American Studies', 'LATINAM 216', 'Music, Politics and Social Change', 'A theorised study of the history of twentieth-century social movements in Latin America through its poetry and music, largely as expressed in popular forms. The major focus is on the political and cultural manifestations of these expressions as they respond to and instigate social change.', 'Prerequisite: 15 points from SPANISH 103, 105, 200, 201, 277, 278, 319, 321, 377, 378', 'null', 'null');
INSERT INTO `arts` VALUES ('Latin American Studies', 'LATINAM 301', 'Music, Politics and Social Change', 'A theorised study of the history of twentieth-century social movements in Latin America through its poetry and music, largely as expressed in popular forms. The major focus is on the political and cultural manifestations of these expressions as they respond to and instigate social change.', 'Prerequisite: 15 points from LATINAM 201, SPANISH 202, 306, or 30 points at Stage II in BGlobalSt courses', 'null', 'null');
