INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 315', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 320', 'Topics in Pragmatics', 'Pragmatics is the systematic study of language in use and is a rapidly developing discipline in linguistics. This course will give a critical survey of the central topics and the latest developments of pragmatics. The domain of pragmatics, implicature, presupposition, speech act and deixis will be among the issues dealt with in individual lectures.', 'Prerequisite: LINGUIST 206', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 322', 'Middle English: Language and Change', 'A study of the origins, development and influences on English until around 1500.', 'Prerequisite: 15 points from LINGUIST 200, 201, ENGLISH 203', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 324', 'Morphology', 'Provides an overview of linguistic morphology and the various strategies of word formation across languages. Theories such as Distributed Morphology, Lexical Phonology and Morphology, Lexeme-Based Morphology, Prosodic Morphology, and Word Syntax will be discussed, and the course will include a practical component in which these theories are applied to language data.', 'Prerequisite: LINGUIST 100 or 103', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 700', 'Directed Study', '</p>', 'null', 'null', 'null');
