INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 704', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 705', 'Field Methods: Phonetics and Phonology', 'Students analyse the structure of an unfamiliar language, focusing on phonetics and phonology. The language studied in LINGUIST 705 and 706 may be the same in any given academic year.', 'Prerequisite: LINGUIST 201 and either LINGUIST 300 or 313', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 706', 'Field Methods: Morpho-syntax', 'Students analyse the structure of an unfamiliar language, focusing on morphosyntax. The language studied in LINGUIST 705 and 706 may be the same in any given academic year.', 'Prerequisite: LINGUIST 201 and either LINGUIST 300 or 313', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 709', 'Linguistic Research', 'Research methods and practices in Linguistics, which provides students with skills necessary for carrying out linguistic research. In addition to practicum sessions students will propose, develop and complete an independent research project.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 720', 'Functional-typological Syntax', 'Cross-linguistic examination of selected topics, such as lexical categories, passives, transitivity, serial verb constructions, head-marking and dependent-marking, and iconicity. Consideration will be given both to differences among languages and to recurrent patterns.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 721', 'Formal Syntax', 'Formal theories of syntax, generative grammar, and current topics of interest to students. This could include: LFG, Minimalism, the DP analysis, theories of argument structure, and/or formal models of language processing.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 722', 'Phonology - Level 9', 'A range of topics from the field of non-linear phonology, including autosegmental phonology, syllable theory, feature geometry and CV phonology.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 724', 'Semantics and Pragmatics - Level 9', 'Deals with a wide range of issues in semantics and especially pragmatics. Topics may include implicature, presupposition, speech act, deixis, reference, pragmatics and cognition, pragmatics and semantics, and pragmatics and syntax.', 'Prerequisite: LINGUIST 206 or 302, or equivalent', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 731', 'Historical Linguistics', 'Current topics in historical linguistics, such as: theories of change in sound systems; syntactic change and syntactic reconstruction; grammaticalisation; distant genetic relationships and comparative methods.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Linguistics', 'LINGUIST 736', 'Issues in Advanced Morphology - Level 9', 'Focuses on competing theoretical models of morphology and includes application of one theory in an extended analysis.\n</p>', 'null', 'null', 'null');
