INSERT INTO `arts` VALUES ('Media, Film and Television', 'MEDIA 229', 'Recorded Music and Media Formats', 'Cultural studies of the sounds and significance of popular recorded music through the media formats in which it has been manufactured, distributed and consumed. Provides a critical introduction to the role of technologies and industries, studios and producers, musicians, music scenes and everyday listening in relation to vinyl records, radio, cassettes, CDs, the MP3 and streaming music.', 'Prerequisite: 15 points from COMMS 100, 104, FTVMS 100, 101, 110, MEDIA 101 and 45 points in BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Media, Film and Television', 'MEDIA 231', 'Eco/media', 'Eco/media introduces students to the increasingly important and varied role that nature, environment, and ecology play in media, film, and television studies. Students explore how environmentalism is communicated through various media, how the mediation of flora, fauna and the earth’s atmosphere offers powerful new insights into media texts, and how media production and consumption can be analysed using ecological frameworks.', 'Prerequisite: 15 points from COMMS 100, 104, FTVMS 100, 101, 110, MEDIA 101 and 45 points in BA courses, or 30 points in Global Environment and Sustainable Development', 'null', 'null');
