INSERT INTO `arts` VALUES ('Museums and Cultural Heritage', 'MUSEUMS 700', 'Exhibiting Cultures: International', 'Investigates the presentation of cultures in museums, art galleries and other sites globally, the strategies of public exhibitions, and the role of curators and institutions in identity formation and nationalism. Case studies are drawn from international and indigenous practice, as well as regional examples from Aotearoa New Zealand, Australia and the Pacific.', 'Restriction: ARTHIST 715, 721, 736, MUSEUMS 701, 703, 704', 'null', 'null');
