INSERT INTO `arts` VALUES ('Mori Studies', 'MAORI 396', 'Tikanga: Ancestral Ways', 'Examines tikanga (ancestral ways of living) and how these have changed since the first arrival of the ancestors of Mori in Aotearoa. Beginning with topics of contemporary interest such as land use, the sea, kinship, gender relations, justice, health and economics, this course will trace the patterns of ancestral life, explore historical debates and reflect upon possible futures.', 'Prerequisite: 30 points at Stage II', 'null', 'null');
INSERT INTO `arts` VALUES ('Mori Studies', 'MAORI 700', 'Reo Mori: Topic in Mori Language', 'An examination of developments in Mori and Polynesian language description, analysis and preservation over the past 50 years.\n</p>', 'null', 'null', 'null');
