INSERT INTO `arts` VALUES ('Mori Studies', 'MAORI 732', 'Rangatiratanga', 'A study of a theoretical framework for the analysis of cultural politics between Mori and Pkeh and between Indigenous Peoples and coloniser States, in particular He Whakaputanga o te Rangatiratanga o N Treni, Te Tiriti o Waitangi and the United Nations Declaration on the Rights of Indigenous Peoples.\n</p>', 'null', 'null', 'null');
