INSERT INTO `arts` VALUES ('Philosophy', 'PHIL 315', 'Topics in Applied Logic', 'A selection of topics in applied logic such as: modal logic (the logic of necessity and possibility), temporal logic (the logic of time), dynamic logic (the logic of change), and epistemic logic (the logic of knowledge and belief, including the logic of belief revision).', 'Prerequisite: 15 points from PHIL 222, 216 or 266', 'null', 'null');
