INSERT INTO `arts` VALUES ('Philosophy', 'PHIL 341', '20th-Century European Philosophy', 'Examines intellectual movements in twentieth-century European philosophy, including phenomenology, hermeneutics, existentialism, and poststructuralism. Discusses key figures in these movements such as Edmund Husserl, Martin Heidegger, Jean-Paul Sartre, Simone de Beauvoir, Maurice Merleau-Ponty, Hannah Arendt, Michel Foucault, and Jürgen Habermas.', 'Prerequisite: 30 points at Stage II in Philosophy, or EUROPEAN 100 and 15 points at Stage II in Philosophy', 'null', 'null');
