INSERT INTO `arts` VALUES ('Philosophy', 'PHIL 762', 'God and Morality', 'An exploration of the relationship between God and morality. Topics may include: the role of moral claims in arguments for or against the existence of God; the impact of morality on disputes about the nature of God, and the influence of theism on the content of morality.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Philosophy', 'PHIL 763', 'Special Topic: Freedom: Its Nature, Value and Future', 'Combines issues in metaphysics, meta-ethics, moral philosophy, philosophy of religion, and political philosophy. The unifying theme is the role of freedom in our moral lives.\n</p>', 'null', 'null', 'null');
