INSERT INTO `arts` VALUES ('Philosophy', 'PHIL 766', 'Special Topic: Ethics for Possible Futures', 'Discusses the impact on moral and political philosophy of two credible futures: a broken world damaged by climate change or other disaster; and a digital future inhabited by super-intelligent machines. How should we think about these futures? Should we welcome or fear them?\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Philosophy', 'PHIL 767', 'Global Justice', 'Examination of issues related to global justice, e.g., What, if anything, are people owed as a matter of justice in the global context? Is global equality of opportunity an important ideal? Which restrictions on immigration, if any, are justified? Are protectionist policies in trade justified? How can we better assist those in poor countries who are trying to help themselves?', 'Restriction: PHIL 310', 'null', 'null');
