INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 100G', 'Beginners\' Russian 1', 'A beginner\'s course using multi-media (computer) materials that presumes no prior knowledge of Russian, with emphasis on a range of language skills – listening comprehension, speaking, reading, writing, and the essential grammar of Russian.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 101', 'Beginners\' Russian 2', 'A continuation of RUSSIAN 100. More practice with written and spoken Russian, fundamental grammar, and authentic texts.', 'Prerequisite: RUSSIAN 100 or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 200', 'Intermediate Russian 1', 'A revision of the grammar covered at Stage I, with more vocabulary building, reading of authentic journalistic and literary texts, and practice of listening and speaking.', 'Prerequisite: RUSSIAN 101 or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 201', 'Intermediate Russian 2', 'Builds on skills obtained in RUSSIAN 200 with special emphasis on practical work, spoken Russian and development of aural-oral skills.', 'Prerequisite: RUSSIAN 200 or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 277', 'Russian Study Abroad 2A', 'Course taken at an approved academic institution abroad.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 278', 'Russian Study Abroad 2B', 'Course taken at an approved academic institution abroad.', 'Prerequisite: RUSSIAN 277 and approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 300', 'Advanced Russian 1', 'A revision of the grammar covered at Stage II, with more emphasis on development of oral and written use of Russian in practical contexts.', 'Prerequisite: RUSSIAN 201 or 210', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 301', 'Advanced Russian 2', 'Builds on skills obtained in RUSSIAN 300, with special emphasis on practical work, increasing fluency and comprehension skills.', 'Prerequisite: RUSSIAN 300', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 377', 'Russian Study Abroad 3A', 'Course taken at an approved academic institution abroad.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 378', 'Russian Study Abroad 3B', 'Course taken at an approved academic institution abroad.', 'Prerequisite: RUSSIAN 377 and approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 390', 'East European Interdisciplinary Essay', 'Students taking this course will write a 6,000-word essay on a historical, political or cultural issue deriving from their prior courses on Eastern Europe, in consultation with one or more of their principal teachers.', 'Prerequisite: 30 points at Stage II or above in History, Political Studies or Politics and International Relations or European Studies and approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Russian', 'RUSSIAN 732', 'Advanced Russian Translation Practice', 'Designed for students who wish to attain expertise in the field of translation. Coursework will consist of correct use of dictionary and reference materials in printed and electronic forms, practice in translation of written material from and into Russian in a variety of registers, critical evaluation of translations.', 'Prerequisite: RUSSIAN 301 or 310', 'null', 'null');
INSERT INTO `arts` VALUES ('Samoan', 'SAMOAN 101', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Samoan', 'SAMOAN 101G', 'Samoan Language 1', 'Gives students an introduction to the structure of Samoan as well as allowing them to develop basic language skills in listening, speaking, reading and writing. Designed for students with little or no knowledge of the language, and for those with some fluency wishing to understand simple sentence structure and composition.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Samoan', 'SAMOAN 201', 'Samoan Language 2', 'Extension of SAMOAN 101 in which more complex sentences will be studied through exposure to reading material and spoken texts such as conversations, speeches, letters, articles, songs and poems.', 'Prerequisite: SAMOAN 101', 'null', 'null');
INSERT INTO `arts` VALUES ('Samoan', 'SAMOAN 203', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Samoan', 'SAMOAN 301', 'Samoan Language 3', 'Conversations and speeches will be studied and practised. The contexts and relationships between ordinary and respectful language levels or honorifics of fa\'asamoa protocols will be examined.', 'Prerequisite: SAMOAN 201', 'null', 'null');
INSERT INTO `arts` VALUES ('Samoan', 'SAMOAN 303', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 200', 'Foundations of Screen Production', 'Students will be exposed to the concepts, working methods and technical aspects of narrative screen production (drama and documentary). Through a series of workshops, exercises and lectures, students will be instructed in basics of production, cinematography, editing, and the broad technical skills required to create well-crafted, proficiently made, time-based projects for the screen.', 'Prerequisite: 30 points from COMMS 100, FTVMS 100, 101, MEDIA 101 and Academic Head or nominee approval', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 201', 'Storytelling for Screen', 'Students will be exposed to the conceptual and practical aspects of constructing a narrative (fiction and non-fiction) for screen.', 'Prerequisite: 30 points from COMMS 100, FTVMS 100, 101, MEDIA 101 and Academic Head or nominee approval', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 202', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 300', 'Documentary Video Making', 'A practical production course in which students work individually and in groups to develop, shoot and edit short documentaries.', 'Prerequisite: SCREEN 200, 201 and approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 301', 'Screenplay Writing', 'A practical screenwriting course in which students work individually to write screenplays for dramatic short films.', 'Prerequisite: SCREEN 200, 201 and approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 302', 'Creating Serial Narratives', 'Enables students to create scripted narratives for television or the web. Examines the history of serial and episodic narratives as well as the current audiences and platforms for both broad and niche shows. Students will conceive, write and produce a serialised narrative.', 'Prerequisite: SCREEN 200, 201 and approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 303', 'Working with Performers for Screen', 'An intensive study of working methods for directing performance of both actors and non-actors as well as non-fiction subjects.', 'Prerequisite: SCREEN 200, 201 and approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 304', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 700', 'Screenwriting Project', 'Considers the short film script with a focus on the practice and principles of dramatic screenwriting, including industry format and narrative structure. By developing a script for a 5-8 minute film in stages, students will practice creative writing, script development, and pitching while also learning skills related to production management, proposal writing, and preparing and scheduling a low-budget production.', 'Restriction: SCREEN 702, 705', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 701', 'Introduction to Directing - Level 9', 'Provides students with a practical overview of the drama and documentary production process from a director\'s point of view. Students are taken through pre-production, production and post-production on a series of group projects, as well as their own 2-minute short film. Emphasises the importance of directing style, character and story alongside acquiring practical skills. Students are encouraged to critique their own work as well as the work of their classmates.', 'Restriction: FTVMS 750', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 702', 'Production Management - Level 9', 'Examines the multiple production roles that exist in film and television production from the producer to the production manager. Students will learn EP Scheduling, budgeting, safety management, casting and general production management skills to aid them in the production process and be introduced to the process of \'creative producing\', including copyright purchasing, identifying appropriate and likely funding sources and market positioning.', 'Restriction: FTVMS 754', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 705', 'Screenwriting - Level 9', 'Considers the short film script with a focus on dramatic writing. Studies a range of short films as a way of understanding this aesthetic form. Designed as a high-intensity, immersion experience in the principles of scriptwriting. Students will write two short scripts beginning with a 2-minute script with minimal dialogue and complete the course with a fully developed 10-minute script for a short film.', 'Restriction: FTVMS 758', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 709', 'Directed Study - Level 9', '', 'Restriction: FTVMS 763', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 710', 'Special Topic - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 711', 'Special Topic - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 712', 'Advanced Drama Directing', 'Focuses on the directing of actors for screen, and the relationship between this and blocking for camera. Students direct in front of the class and create a short piece of work as a group using improvisation. Students first focus on directing and presenting a 10-minute excerpt from a play and then direct and edit an original dramatic short film of 8-10 minutes. Crewing on fellow students\' projects is a compulsory requirement.', 'Prerequisite: SCREEN 701', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 713', 'Advanced Documentary Directing', 'Students complete a major treatment/script, an interview exercise, an editing exercise, and a completed documentary of 10-12 minutes. For the latter project, the course convener functions like an executive producer, overseeing and critiquing the documentaries as they progress. Emphasis is placed on aesthetic and formal approaches to the documentary and the class will draw extensively on documentary history.', 'Restriction: COMMS 713, FTVMS 753, SCREEN 704', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 714', 'Advanced Screenwriting: Drama', 'Focuses on screenplay writing for feature films. Through a combination of lectures and workshops, students will take individual feature-length projects from pitch to step outline. The course emphasises plot, structure, character, and dialogue. Students will also gain skills in analysing screenplays and writing treatments.', 'Prerequisite: SCREEN 705 or equivalent', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 715', 'Directed Study', 'Directed research on a selected topic.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 797A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Screen Production', 'SCREEN 797B', 'Production Project - Level 9', 'The production of a substantial project in which the student specialises as director, writer, or producer completing either a documentary (approximately 30 minutes), a short dramatic film (approximately 10-15 minutes) or a feature length screenplay (80-110 pages). Students are required to attend a seminar series conducted by academic staff and industry practitioners in Semester One. Crewing on fellow students\' projects is also required.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Social Science Research Methods', 'SOCSCRES 200', 'Mixing and Matching Methods', 'Explores methods and methodologies from both qualitative and quantitative traditions (interviewing, participant observation, forms of textual analysis, genre studies; as well as, surveys, content analysis, material trace analysis, statistical approaches. Focuses also on approaches to research that combine qualitative quantitative methods.', 'Prerequisite: SOCSCRES 100 or 60 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Social Science Research Methods', 'SOCSCRES 300', 'Working with Numerical Data', 'Intensively studies collection, access and analysis of statistical data. The course will extend competencies in the use of statistical analysis, as well as examining digital and technologically-mediated environments that produce big data. A key focus will be in the transformation of data generated and collected by a range of public and private stakeholders to material amenable for contemporary social science research.', 'Prerequisite: SOCSCRES 200 or 30 points at Stage II from BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Social Science Research Methods', 'SOCSCRES 301', 'Skills in Qualitative Research', 'Intensively studies the methods and methodologies of qualitative research (e.g., indigenous, Pacific, feminist), as well as current debates. Examines issues raised when studying people, communities and cultures, and considers archival, digital and other innovations in collecting and representing data. It explores a wide range of methods used to collect and analyse qualitative data, for example, interviews, focus groups, participant observation, and thematic analysis.', 'Prerequisite: SOCSCRES 200 or 30 points at Stage II from BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Social Science for Public Health', 'SOCSCIPH 200', 'Social Science for Health', 'Explores diversity in health knowledges, offers an overview of current health trends and systems with special focus on New Zealand and the Pacific, examines the contributions of social science disciplines to analysis of health including key theoretical approaches, and applies interdisciplinary analytical models to health.', 'Prerequisite: 30 points at Stage I in Health Social Sciences or Social Science for Public Health, or 60 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Social Science for Public Health', 'SOCSCIPH 300', 'Current Debates in Health and Health Policy', 'Examines the recent histories of central intellectual debates in health and health policy and their relevance for and in the New Zealand and Pacific contexts. These may consist of the following: the \'medicalisation\' of social issues, the \'socialisation\' of medical issues, cross-national health policy analysis, the rationing of health resources (global and local perspectives), defining and measuring health outcomes (accountability and responsibility in health service delivery), health service management (medics or managers).', 'Prerequisite: SOCSCIPH 200', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 100', 'Issues and Themes in Sociology', 'Introduction to sociology as a discipline and a review of some of its internal debates. Topics include: social class, gender, globalisation, power, sexual identity and family. Draws on material from a range of societies.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 101', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 101G', 'Understanding Aotearoa New Zealand', 'Provides an introduction to the sociological analysis of New Zealand society. Looks at familiar events, institutions, social processes from a sociological point of view and offers ways to understand them in new and different ways. Focuses on the structure of New Zealand society and on social and political changes which affect the lives of New Zealanders and shape their society.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 103', 'New Zealand Social Policy and Social Justice', 'Provides an overview of key contemporary social policy issues within the context of globalising economic processes and continuing gendered and racialised divisions. Discusses the way in which debates around social policy are constructed and the implications this has for social justice. Case studies may include food and health, technology, indigeneity and children.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 105', 'Cultural Studies and Society', 'A cultural studies approach to social life focuses on the way we experience the world, taking account of what we see, what we hear, what we consume and how we communicate. Sociological theory will be explored through investigating different cultural forms including film, advertising, art, social media, sport, and video games.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 200', 'Sociological Theory', 'An introduction to the major themes of social theory. The focus is on the analyses of modern society to be found in the works of classical and contemporary social analysts.', 'Prerequisite: 30 points at Stage I in Sociology or 15 points at Stage I in Sociology with a minimum B+ pass', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 203', 'Social Reality and Ideology', 'Sociologists construe ideology as ideas that conceal social inequalities. This course explores the meaning of ideology and some of its related concepts, such as hegemony, discourse, and subjugation. It then critically analyses some of the most pervasive contemporary ideologies. Although not exhaustive, examples include beliefs about personal responsibility, corporate job creators, faith in technology, and crime and deviance.', 'Prerequisite: 60 points passed from BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 204', 'Special Topic', '', 'Prerequisite: 30 points at Stage I in Sociology or 15 points at Stage I in Sociology with a minimum B+ pass', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 205', 'Special Topic', '', 'Prerequisite: 30 points at Stage I in Sociology or 15 points at Stage I in Sociology with a minimum B+ pass', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 206', 'Sociology for Auckland', 'Auckland is located in Aotearoa New Zealand but is not always of Aotearoa New Zealand. By virtue of geography, history, and size, Auckland is a unique social setting, with an uneasy relationship to the rest of Aotearoa New Zealand (and beyond). This course examines the differences in class, ethnicity, gender, sexual orientation, and other social factors that make Auckland “special”.', 'Prerequisite: 30 points at Stage I in Sociology or 15 points at Stage I in Sociology with a minimum B+ pass', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 207', 'Sociology of Gender and Families', 'Focuses on the interrelationship between gender, sexuality and families in New Zealand and other Western societies. Through an examination of important moments in the life course of families – for example, partnering and parenting – it explores changes and continuities in the gendered norms, identities, practices and patterns that characterise contemporary family life.', 'Prerequisite: 30 points in Sociology or Gender Studies or 60 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 208', 'Economy and Society', 'Examines the changing relations between work and life outside of paid employment. Particular attention is paid to new forms of expropriation that profit from claiming private ownership of collective effort, ideas and cultural forms. These developments are crucial to understanding and contesting social inequality, globalisation, organisational restructuring and new technologies. Course material is drawn from international literatures and is grounded in an understanding of contemporary New Zealand.', 'Prerequisite: 30 points at Stage I in Sociology or 15 points at Stage I in Sociology with a minimum B+ pass, or 30 points in International Relations and Business', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 210', 'Colonisation, Globalisation and Social Justice', 'Charts the political, economic, cultural and ecological consequences of imperialism, colonisation, globalisation, aid and development, up to and including the IMF/World Bank\'s neoliberalism and structural adjustment programme. It pays particular attention to violence (physical, psychological and ideological), environmental consequences and health impacts in these contexts.', 'Prerequisite: 30 points at Stage I in Sociology or 15 points at Stage I in Sociology with a minimum B+ pass or LATINAM 100 or SPANISH 103, or 30 points in Global Politics and Human Rights', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 211', 'Sociology of Popular Culture', 'Popular culture appears to be everywhere, but what political and social effects might all this popular entertainment have on us? This course seeks to answer such questions through a sociological interpretation of popular culture as both an indicator of social change and as a location of meaning and significance. Topics include reality TV, celebrities, consumption, music, and technology.', 'Prerequisite: 30 points at Stage I in Sociology, or 15 points at Stage I in Sociology with a B+ or higher, or 30 points from COMMS 100, FTVMS 100, 101, MEDIA 101, or 30 points from Comparative Literature', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 213', 'Ethnicity and Identity', 'Charts the development of the concepts of racial, national, ethnic and indigenous identities in relation to the histories of modernity and colonisation and then uses these concepts to analyse a range of contemporary issues of identity and belonging.', 'Prerequisite: 30 points at Stage I in Sociology or 15 points at Stage I in Sociology with a minimum B+ pass', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 228', 'Special Topic: Sociology of Migration', 'Examines international migration in the context of the modern world economy, its impact on global cultures and its relationships with ideologies such as nationalism and racism. The focus is on the Pacific Rim region, but in the context of, and in comparison with, other parts of the world.', 'Prerequisite: 30 points at Stage I in Sociology or 15 points at Stage I in Sociology with a minimum B+ pass', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 229', 'Environmental Sociology', 'Environmental sociology provides insight into the complex social processes that define, create and even threaten our natural environment. This course gives tools with which to think sociologically about environmental issues, such as understanding how environmental issues come to be seen as environmental problems, and how political, cultural, and economic factors have come to shape our interaction with the natural environment.', 'Prerequisite: 30 points at Stage I in Sociology or 30 points from ENVSCI 101, 201, GEOG 102, 205', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 300', 'Sociology of Science and Technology', 'A survey of theoretical and practical approaches to understanding the relationship between technology and society. Topics studied include: technology and social theory, technology and subjectivity, the politics of artefacts and the impacts of new social media.', 'Prerequisite: 30 points at Stage II in Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 301', 'Critical Theory and Society', 'Critical theory seeks to understand the multiple contradictions of society and to offer roadmaps for progressive social change. This course explores foundational ideas in the tradition of critical theory and in contemporary critical thought. Critical theories are situated in the social and historical contexts from which they arise and are scrutinised for their relevance to contemporary struggles for social justice.', 'Prerequisite: SOCIOL 200 or 30 points above Stage I in BA or Global Studies courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 305', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 306', 'Sociology of Migration', 'Examines international migration in the context of the modern world economy, its impact on global cultures and its relationships with ideologies such as nationalism and racism. The focus is on the Pacific Rim region, but in the context of, and in comparison with, other parts of the world.', 'Prerequisite: 30 points at Stage II in Sociology or 15 points at Stage II in Sociology and 30 points at Stage II in BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 307', 'The Pacific in the World', 'Brief history of post Second World War theories of economic development and modern world systems. Explores Pacific responses to world systems such as colonisation, capitalism, globalisation and militarisation. Examines Pacific relations with colonial and imperial powers such as Aotearoa New Zealand, Britain, and US. Analyses the limitations of world systems models.', 'Prerequisite: 30 points at Stage II in Global Environment and Sustainable Development or Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 310', 'Researching Social Problems', 'Develops skills in integrating theory and methods so that students can critically engage with social problems using a sociological lens both inside and outside the university.', 'Prerequisite: 30 points at Stage II in Sociology or 60 points passed at Stage II from BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 315', 'Law, Inequality and the State', 'Examines, in a comparative mode, how law as a set of social relations and categories can both create and remedy inequalities of gender, race, and class.', 'Prerequisite: 30 points at Stage II in Sociology or 15 points from CRIM 201, 202 or 30 points at Stage II in Global Politics and Human Rights', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 316', 'Critical Theories of Schooling', 'Compulsory schooling in western society has traditionally been seen as a significant instrument of socialisation, progression and economic advancement for young people. The course will engage students in ideas which challenge this view by drawing on critical theories such as Labelling, Marxist, Foucauldian, and Anarchist theory, and exploring topics including colonialism, patriarchy, racism, and the social control of youth.', 'Prerequisite: 30 points at Stage II in Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 317', 'Changing Welfare State Policies', 'Examines the differing origins and possible futures of welfare states. Using a range of theoretical viewpoints, the course considers the historical shift from a Keynesian welfare state to a neo-liberal state and questions whether recent ‘Third Way’ policies provide a solution to future welfare needs. The course is comparative, but will concentrate mostly on Aotearoa New Zealand.', 'Prerequisite: 30 points at Stage II in International Relations and Business or Sociology, or SOCSCIPH 200', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 318', 'Sociology of the Media', 'An exploration of the relationship and patterns of interaction between media, culture and society through an examination of the print and broadcasting media, and advertising in New Zealand.', 'Prerequisite: 30 points at Stage II in Sociology, or Media, Film and Television, or Communication', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 322', 'A Sociology of Relational Life', 'Introduces students to new developments in sociology by examining the significance of our relationships to others: intimate partners, friends, acquaintances, and even pets. The course considers the ways relationships are embedded in life through everyday practices, sharing photographs, and telling stories. In so doing, it engages with contemporary debates about the rise of individualism and the decline of family life.', 'Prerequisite: 30 points at Stage II in Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 323', 'Sociology of Consumption', 'Approaches consumption as an economic imperative, a way of life, and a condition of subjectivity. Examining the environmental impact of consumption and conditions under which those who labour to produce the things we consume live, consideration will be given to the symbolic power of consumption and how class, gender, and racial divisions are reproduced through consumption habits.', 'Prerequisite: 30 points at Stage II in Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 326', 'Sociology of Violence and Death', 'Drawing on writings from a variety of intellectual traditions, this course explores the contested nature of violence through an examination of a number of contemporary debates about the causes, agents, consequences, as well as responses to and interventions in, incidents of violence.', 'Prerequisite: 30 points at Stage II in Sociology, or 15 points at Stage II in Sociology and CRIM 201 or 202', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 330', 'Special Topic', '', 'Prerequisite: 30 points at Stage II in Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 333', 'Sociology of Health, Illness, and Medicine', 'Presents a conceptual and topical overview of the Sociology of Health, Illness, and Medicine. Specific topics to be addressed include: the social distribution of disease; the social production of disease; the social construction of \'illness\'; the social construction of treatment practices; patient experiences of illness and healthcare; the social organisation of medicine; and alternative visions of healthcare.', 'Prerequisite: 30 points at Stage II in Sociology, or SOCSCIPH 200', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 334', 'Youth Sociology', 'Introduces students to literature in the social constructions of youth identity in society by exploring relationships between youth identity, crime, deviance, public policy, media and moral panics, popular culture, class, gender and ethnicity in modern society using \'youth-focused\' theoretical and empirical work.', 'Prerequisite: 60 points passed at Stage II in the BA', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 335', 'Sociology of Work and Organisation', 'Examines political and textual representations in complex organisations. Political readings of organisations emphasise the social forces and institutional frameworks by which sets of actors represent others. Textual readings emphasise ways in which symbolic realities come to stand for material ones. Explores the differing expressions of representation across organisations and the convergence and divergence of political and textual readings.', 'Prerequisite: 30 points at Stage II in Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 339', 'Special Topic', '', 'Prerequisite: 30 points at Stage II in Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 340', 'Special Topic', '', 'Prerequisite: 30 points at Stage II in Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 700', 'Advanced Problems in Sociological Theory', 'Addresses contemporary developments and debates in the field of social theory and explores the effect of a range of complex social, cultural, economic and political processes of transformation on human experience and conduct.', 'Restriction: SOCIOL 733', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 701', 'Advanced Skills in Research', 'Examination of sociological issues in research design, execution, analysis and interpretation. Particular attention is paid to computer assisted data and benefits of employing multiple methods.', 'Restriction: SOCSCRES 702, 703', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 703', 'Sociology of Mental Health', 'Interrogates advanced sociological theories of medicine and psychiatry, investigating mental health interventions as social, economic, cultural and political projects. Key issues will include The Enlightenment and theories of the self, the rise of science and the \'psy\' professionals, institutionalisation and community care, current sociological theories of mental health, the medicalisation of everyday life, and gender, race and mental illness.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 706', 'Special Topic: The Sociology of Disasters - Level 9', 'Disasters are increasing in scale, cost, frequency and severity. This course examines their causes and consequences and considers their future avoidance. In doing so it draws on social theory, Science and Technology Studies, and broad literatures on disaster. Topics include: the risk society thesis, the social patterning of disasters, and the political economy of disasters (disaster capitalism).\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 707', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 709', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 713', 'Sociology of Law: Human Rights', 'Examines recent developments in the sociology of human rights within the study of law and society. Sociological thinking can offer a critical examination of international rights thought and practice, addressing the place of rights discourse in law and literature, feminist issues, indigenous rights, the place of rights in critical race theory and for refugees.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 718', 'Research Projects: Design and Practice', 'Explores some of the major principles of research design and practice before discussing particular methods of research. Students will work through problem definition, literature review, and research design. Both ‘empirical’ and ‘theoretical’ projects will be encouraged.', 'Restriction: SOCIOL 731, SOCSCRES 701', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 728', 'Family, Gender and the State', 'The influence of changing ideologies of mothering, fathering, family and work, as well as the political forces and pressure groups that promote them, on both the development and restructuring of social and legal policies in several industrialised nations, including New Zealand.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 732', 'Tourism and Heritage', 'Tourism is one of the world\'s largest and fastest growing industries. This course examines tourism\'s intersection with \'heritage\', considering the political economy of tourist development, the tourist gaze, commodified \'authenticity\' and cultural conflicts in tourism.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 735', 'Current Debates in Gender and Sexuality', 'Traces contemporary sociological debates in the analysis of gender and sexuality. This includes reference to feminist concerns with identities, differences, bodies, power and agency. These issues are taken up and explored through an examination of practices enacted on/or through sexed bodies.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Sociology', 'SOCIOL 736', 'Renegotiating Citizenship', 'Changing economic, political and social realities in Western societies have challenged traditional notions of citizenship. This course critically examines emerging debates in citizenship studies that consider how shifting political ideologies, welfare state reform, increasing cultural diversity and globalisation impact on citizenship at both theoretical and policy levels.\n</p>', 'null', 'null', 'null');
