INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 104G', 'Beginners\' Spanish 1', 'Provides a solid grounding in the basic grammar and vocabulary of Spanish for beginners or near beginners, emphasising communicative competence in the present tense. Develops speaking, listening, reading and writing skills, and prepares students at the A1 Level of the Common European Framework of Reference for Languages.', 'Restriction: SPANISH 107. May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 105', 'Beginners\' Spanish 2', 'Moving from the present to the past tenses, this course prepares students for basic conversation about everyday activities such as travel, weather, health, pastimes, emotions and simple narratives of historical events. Prepares students for the A2 Level of the Common European Framework of Reference for Languages.Note: Students with 16 Level 2 NCEA credits in Spanish in last two years will enrol in SPANISH 105.', 'Prerequisite: SPANISH 104 or 109', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 178', 'Spanish Study Abroad 1B', 'Course of at least 4 weeks in length and 48 taught hours on Spanish language and/or culture to be taken at an approved academic institution in a Spanish-speaking country.', 'Prerequisite: B- or higher in SPANISH 104 or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 200', 'Intermediate Spanish 1', 'Consolidates Spanish study by introducing the present subjunctive, enables students to move toward fluency in conversations on contemporary topics, and enriches daily activities with detail, subtlety and idioms used in the context of the rich cultures of the Hispanic world (Spain and Latin America). This course is equivalent to B1 of the Common European Framework of Reference for Languages.', 'Prerequisite: SPANISH 105 or 178', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 201', 'Intermediate Spanish 2', 'Builds on skills obtained in SPANISH 200 with special emphasis on practical work, spoken Spanish and development of aural-oral skills.', 'Prerequisite: SPANISH 200', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 202', 'Iberian Cultures and Literatures', 'An introduction to the study of Iberian literatures in their cultural contexts, focusing on major works and movements from different historical periods.', 'Prerequisite: 15 points from SPANISH 105, 108, 200, 201, 277, 278, 319, 321, 377, 378', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 203', 'Iberian and Latin American Civilisations', 'Focuses on the unique traditions and radical innovations of the pluricultural nations comprising the Iberian Peninsula and Latin America, tracing parallel historical trajectories, diverse political systems and engaging aesthetic creations. Develops knowledge of Spanish and Latin American cultural studies through a global studies approach.', 'Prerequisite: 45 points at Stage I in BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 206', 'Spanish Myths and Global Icons', 'Explores cultural representations of universal Spanish motifs in literature and the arts. Examines classical cultural myths associated with Spain (such as Don Quixote, Don Juan and Carmen), and global icons which have defined modernity, from Picasso, Dalí, and García Lorca to Buñuel and Almodóvar.', 'Prerequisite: 15 points from SPANISH 105, 108, 200, 201, 277, 278, 319, 321, 377, 378', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 207', 'Transnational Movements in Hispanic Culture', 'Explores transnational movements pertaining to Spain and Latin America: from the impact of Catholicism and Jewish and Islamic cultures on early modern Spain and its colonised territories, to the nineteenth-century agendas of abolitionism and freethinking, to exile, gender and human rights movements in the twentieth and twenty-first centuries.', 'Prerequisite: 15 points from SPANISH 105, 108, 200, 201, 277, 278, 319, 321, 377, 378', 'null', 'null');
