INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 278', 'Spanish Study Abroad 2B', 'For approved courses at overseas institutions with permission of the Academic Head or nominee.', 'Prerequisite: B- or higher in SPANISH 105 or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 302', 'Iberian Cultures and Literatures', 'Advanced study of Iberian literatures in their cultural contexts, focusing on major works and movements from different historical periods.', 'Prerequisite: 15 points from SPANISH 201, 278, 319, 321, 323, 377, 378 and 15 points from SPANISH 206, 207, LATINAM 201, 210, 216', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 306', 'Spanish Myths and Global Icons', 'Explores cultural representations of universal Spanish motifs in literature and the arts. Examines classical cultural myths associated with Spain (such as Don Quixote, Don Juan and Carmen), and global icons which have defined modernity, from Picasso, Dalí, and García Lorca to Buñuel and Almodóvar.', 'Prerequisite: 15 points from SPANISH 201, 278, 319, 321, 377, 378 and 15 points from SPANISH 202, 207, LATINAM 201, 216', 'null', 'null');
