INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 310', 'Gender Perspectives on Hispanic Literature', 'An examination of a selection of Hispanic literary texts in the light of contemporary gender studies.', 'Prerequisite: SPANISH 201 or 278 or 319 or 321 or 377 or 378 and 15 points from SPANISH 202 or LATINAM 201 or 216', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 313', 'Engendering Nations', 'The debates on the gendered heritage of modernity in Spain\'s and/or Latin America\'s nation-building projects, through the study of modern national fictions.', 'Prerequisite: SPANISH 201 or 278 or 319 or 321 or 377 or 378 and 15 points from SPANISH 202 or LATINAM 201 or 216', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 315', 'Special Topic', '', 'Prerequisite: SPANISH 201 or 278 or 319 or 321 or 377 or 378 and 15 points from SPANISH 202 or LATINAM 201 or 216', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 316', 'Special Topic', '', 'Prerequisite: SPANISH 201 or 278 or 319 or 321 or 377 or 378 and 15 points from SPANISH 202 or LATINAM 201 or 216', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 317', 'Hispanic Cultures in Cinema', 'A study of Spanish and/or Latin American cultures and their representation in films. Emphasis on critical theories and cultural contexts of representation.', 'Prerequisite: SPANISH 201 or 278 or 319 or 321 or 377 or 378 and 15 points from SPANISH 202 or LATINAM 201 or 216', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 318', 'Making Modern Spain 1840-1939', 'The making of modern Spain charts a period in which gender, class and ideological upheavals intersect with enquiry and debate as to what constitutes the Spanish nation. An overview of key moments of Spanish cultural politics from high Romanticism through to the end of the Spanish Civil War, examining the connections between seduction, both personal and intellectual, and social revolutions.', 'Prerequisite: SPANISH 201 or 278 or 319 or 321 or 377 or 378 and 15 points from SPANISH 202 or LATINAM 201 or 216', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 319', 'Advanced Spanish 1', 'Advanced study of Spanish language.', 'Prerequisite: SPANISH 201 or 278', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 321', 'Advanced Spanish 2', 'Builds on skills obtained in SPANISH 319 with special emphasis on advanced Spanish grammar review and development of Spanish and Latin American cultural literacy. Note: Students who have passed SPANISH 300 in 2006 or 2007 may enrol in the course with permission of the Academic Head or nominee.', 'Prerequisite: SPANISH 319 or 377 or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 323', 'Spanish Translation Practice', 'Develops translator competence within general, cultural and technical knowledge domains, through full translations into and out of Spanish, topic-based research, and summary and selective translations. Specific skills include understanding the different phases in the translation process; documentary research skills, and editing and proof-reading skills.', 'Prerequisite: SPANISH 201 or approval of Spanish Programme Coordinator', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 341', 'Spanish Sound Structure', 'Provides advanced Spanish learners with a solid foundation in Spanish phonetics and phonology. Spanish sounds are explained and practiced in order to minimise native-language transfer. Students learn articulatory phonetics, phonetic transcription, and sound-pattern recognition, skills that enable them to discern native pronunciations and discover the principles that underlie the Spanish sound system.', 'Prerequisite: 15 points from SPANISH 201, 278, 319, 321, 377, 378', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 342', 'Spanish Word Formation', 'An introduction to the formal study of Spanish words and the processes that generate them. Key morphological concepts explain how words may be related. The processes used to derive words and create grammatical variants will be analysed and practiced. Construction and deconstruction of words will be examined with reference to the enrichment of vocabulary.', 'Prerequisite: 15 points from SPANISH 201, 278, 319, 321, 377, 378', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 345', 'Peripheric Cultures and Literatures', 'Focuses on the study of peripheric cultures and literatures in the Hispanic world, historically marginalised by issues of language, ethnicity, and/or geopolitical positioning, which have excluded them from traditionally centralist, homogeneous and monolithic definitions of the nation.', 'Prerequisite: SPANISH 201 or 278 or 319 or 321 or 377 or 378 and 15 points from SPANISH 202 or LATINAM 201 or 216', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 350', 'Directed Reading and Research', 'Supervised research projects.', 'Prerequisite: SPANISH 201 or 278 or 319 or 321 or 377 or 378 and 15 points from SPANISH 202 or LATINAM 201 or 216, and approval of Academic Head or nominee', 'null', 'null');
