INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 718', 'Hispanic Cultures in Cinema', 'A study of Spanish and/or Latin American cultures and their representation in films. Emphasis on critical theories and cultural contexts of representation.', 'Restriction: SPANISH 317', 'null', 'null');
