INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 720', 'Latin American Knowledges', 'An examination of new knowledges produced in Latin America that have influenced socio-political theory and global epistemological paradigms but are subalternised as art, culture, or politics. Therefore, this course will examine the link between theory and practice in the creation of new knowledge.', 'Prerequisite: LATINAM 301, or LATINAM 306, 325, POLITICS 332', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 722', 'Gender Perspectives on Hispanic Literature', 'An examination of a selection of Hispanic literary texts in the light of contemporary gender studies.', 'Restriction: SPANISH 310', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 723', 'Advanced Spanish Translation Practice', 'Aims at developing translator competence within general, cultural and technical knowledge domains, through full translations into and out of Spanish, topic-based research, and summary and selective translations. Specific skills include mastering the different phases in the translation process; understanding the main textual and contextual features of Languages for Specific Purposes (LSP) texts; documentary research skills, and editing and proof-reading skills.', 'Restriction: SPANISH 323', 'null', 'null');
INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 725', 'Making Modern Spain 1840-1939', 'The making of modern Spain charts a period in which gender, class and ideological upheavals intersect with enquiry and debate as to what constitutes the Spanish nation. An overview of key moments of Spanish cultural politics from high Romanticism through to the end of the Spanish Civil War, examining the connections between seduction, both personal and intellectual, and social revolutions.', 'Restriction: SPANISH 318', 'null', 'null');
