INSERT INTO `arts` VALUES ('Spanish', 'SPANISH 738', 'Engendering Nations', 'The debates on the gendered heritage of modernity in Spain\'s and/or Latin America\'s nation-building projects through the study of modern national fictions.', 'Restriction: SPANISH 313', 'null', 'null');
