INSERT INTO `arts` VALUES ('TFC Academic English Studies', 'TFCACENG 93F', 'Foundation Academic English', 'Develops language skills necessary for academic study. The course will help students to improve their sentence structure, develop their vocabulary, plan their writing, and improve paragraph and essay organisation. It focuses on sound principles of essay writing and provides practice in the various stages of writing.', 'Prerequisite: Coordinator approval', 'null', 'null');
INSERT INTO `arts` VALUES ('Teritary Foundation Certificate English', 'TFCENG 91F', 'Academic Literacy 1', 'Establishes skills in spoken and written English for academic purposes. Introduces critical reading, writing, listening and oral presentation skills. Students develop greater competency in English and learn the basics of academic literacy.', 'Restriction: ENGLISH 91F', 'null', 'null');
INSERT INTO `arts` VALUES ('Teritary Foundation Certificate English', 'TFCENG 92F', 'Academic Literacy 2', 'Further establishes English skills for academic purposes. Develops sound academic practice, including enhanced critical reading and writing, critical listening and effective oral presentation skills. Building on TFCENG 91F, this course strengthens students’ abilities and confidence in academic literacy.', 'Restriction: ENGLISH 92F', 'null', 'null');
INSERT INTO `arts` VALUES ('Tertiary Foundation Certificate Arts General', 'TFCARTS 92F', 'Introduction to Arts and Humanities', 'Encourages students to think in a variety of different ways about a particular theme or topic. This is achieved by introducing different Arts and Arts-related subjects/disciplines and the ways those different disciplines can, in their own unique ways, work complementarily to enrich understanding.', 'Restriction: ARTSGEN 92P', 'null', 'null');
INSERT INTO `arts` VALUES ('Tertiary Foundation Certificate English Writing', 'TFCEWRIT 94F', 'Foundation English Writing', 'Develops language skills necessary for academic study. The course will help students to improve their sentence structure, develop their vocabulary, plan their writing, and improve paragraph and essay organisation. It focuses on sound principles of essay writing and provides practice in the various stages of writing.', 'Prerequisite: Coordinator approval', 'null', 'null');
INSERT INTO `arts` VALUES ('Tertiary Foundation Certificate History', 'TFCHIST 91F', 'Foundation History', 'Examines New Zealand history between 1769 and 1840, particularly the interactions and relationships between the Mori world and Europeans and their ongoing legacies to today. Introduces broader skills suitable for studying history, providing a solid foundation for research, critical thinking, speaking, writing and collaborative work that is invaluable in a wide-range of disciplines.', 'Restriction: HISTORY 91F', 'null', 'null');
INSERT INTO `arts` VALUES ('Tertiary Foundation Certificate Pacific Studies', 'TFCPAC 91F', 'Foundation Pacific Studies', 'Introduces students to an essential knowledge of the Pacific and its cultures and peoples, and to the core practices and concepts of interdisciplinary Pacific Studies. This course will provide a foundation of knowledge of Pacific cultures, languages, history, geography and politics, and introduce students to some core Pacific Studies concepts (such as fa’aalo’alo/faka’apa’apa, diaspora, Oceania, identity, and culture). Students will gain familiarity with the history and purposes of Pacific Studies and work with some accessible forms of indigenous Pacific knowledge.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Tertiary Foundation Certificate Sociology', 'TFCSOCIO 91F', 'Foundation Sociology 1', 'Introduces students to fundamental building blocks in sociology. Students develop familiarity with key sociological concepts that explain social inequalities, enabling them to think sociologically about this issue. In particular, students learn how social structures (for example, class, race/ethnicity, gender and sexuality), social institutions (for instance, the state) as well as interactions between people produce and sustain various forms of inequality.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Tertiary Foundation Certificate Sociology', 'TFCSOCIO 92F', 'Foundation Sociology 2', 'Focuses on Aotearoa New Zealand as a multicultural nation produced through colonisation and subsequent waves of migration. Using a range of case studies, the course highlights the role of different cultural norms and values in creating diverse experiences of living in Aotearoa New Zealand. Such divergent realities invite reconsideration of what it means to be a ‘New Zealander’.\n</p>', 'null', 'null', 'null');
