INSERT INTO `arts` VALUES ('Tongan', 'TONGAN 101G', 'Tongan Language 1', 'Gives students an introduction to the structure of Tongan as well as allowing them to develop basic language skills in listening, speaking, reading and writing. Designed for students with little or no knowledge of the language, and for those with some fluency wishing to understand simple sentence structure and composition.', 'Restriction: May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Tongan', 'TONGAN 201', 'Tongan Language 2', 'Extends language fluency developed in TONGAN 101 by progressively introducing more challenging reading and writing tasks, such as narrating myths and legends and describing aspects of Tongan culture.', 'Prerequisite: TONGAN 101', 'null', 'null');
