INSERT INTO `businessandeconomics` VALUES ('Academic Integrity', 'ACADINT A01', 'Academic Integrity Course', 'The Academic Integrity Course is an online course designed to increase student knowledge of academic integrity, university rules relating to academic conduct, and the identification and consequences of academic misconduct. Students work through a series of modules, outlining scenarios that they may encounter while studying at university. Each scenario provides information on relevant rules, resources and expected behaviour.\n</p>', 'null', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 101', 'Accounting Information', 'Business decisions require accounting information. This course examines the analysis and interpretation of general-purpose financial statements. It assesses financing and investment decisions and covers the role of accounting information to support planning and control.\n</p>', 'null', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 102', 'Accounting Concepts', 'Basic principles and concepts of accounting that underlie the production of information for internal and external reporting. This course provides the technical platform for second year courses in financial and management accounting, finance, and accounting information systems.', 'Prerequisite: ACCTG 101', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 151G', 'Financial Literacy', 'People who understand the basic principles of finance are likely to get much more mileage out of their money – whether spending, borrowing, saving or investing – than those who do not. Develop an understanding of how to be in control of spending and saving; understand borrowing; make informed investment decisions; know broadly what to insure and what not to; recognise scams and consider whether money is the key to happiness.', 'Restriction: May not be taken by students with a concurrent or prior enrolment in Accounting or Finance courses', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 211', 'Financial Accounting', 'The study of financial accounting principles within New Zealand, to enable students to: (i) understand how they are developed and influenced; (ii) understand and apply New Zealand Financial Reporting Standards; (iii) report the results of complex business structures involving multiple entities and segments. Completing students will understand the role financial statements play in investment, analysis and contracting decisions, providing a base for advanced study and supporting other areas, particularly finance.', 'Prerequisite: ACCTG 102, STATS 108', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 221', 'Cost and Management Accounting', 'Budgets and standards, costing systems, cost information for decision-making and control, performance appraisal, and contemporary related issues.', 'Prerequisite: ACCTG 102, STATS 108', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 222', 'Accounting Information Systems', 'Encompasses the development and distribution of economic information about organisations for internal and external decision-making. Major themes include: objectives and procedures of internal control, the database approach to data management, data modelling, typical business documents and reports and proper system documentation through data flow diagrams and flowcharts.', 'Prerequisite: ACCTG 102, INFOSYS 110', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 311', 'Financial Accounting', 'Explanatory and prescriptive theories of accounting provide the context for an examination of the determinants of financial reporting practice in New Zealand with special reference to accounting for pensions, foreign currency, deferred tax and financial instruments. Issues in international accounting and professional ethics are also addressed.', 'Prerequisite: ACCTG 211', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 312', 'Auditing', 'An introduction to the audit of financial statements. The objective of an audit is to add credibility to the information contained in the financial statements. Emphasises the auditor\'s decision-making process in determining the nature and amount of evidence necessary to support management\'s assertions. The end result of a financial statement audit is a report that expresses the auditor\'s opinion on the fair presentation of the client\'s financial statements.', 'Prerequisite: ACCTG 211 or 292, and INFOMGMT 296 or 294 or ACCTG 222', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 321', 'Strategic Management Accounting', 'A study of the design of revenue and cost management systems to facilitate strategic decisions. This includes activity-based costing and activity-based management. The learning environment is student-centred with the seminar leader\'s role being that of facilitator rather than lecturer. Students work not only as individuals but also in teams. The learning environment is a combination of lectures, case studies and related readings.', 'Prerequisite: ACCTG 221 or 291', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 323', 'Performance Measurement and Evaluation', 'The design of performance measurement frameworks such as the Balanced Scorecard incorporating strategy maps and alignment principles. Methods of performance analysis will cover ratios, weighting systems and Data Envelopment Analysis. Evaluation principles and methods will include internal audit perspectives around project and programme evaluation, cost-benefit analysis, randomised control tests and value-for-money.', 'Prerequisite: Any 30 points at Stage II in Accounting or Finance', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 331', 'Revenue and Cost Management', 'Revenue management concepts and techniques and their support by cost management systems are studied with particular reference to service organisations. The range of services encompasses both private and public sector organisations. Components include: yield management, pricing, linear programming, project management, valuation principles and methodologies.', 'Prerequisite: ACCTG 221 or 291', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Accounting', 'ACCTG 371', 'Financial Statement Analysis', 'How is financial statement information used to evaluate a firm\'s performance, risk and value? An opportunity to examine this question and to gain experience in evaluating performance, assessing risk and estimating value.', 'Prerequisite: FINANCE 251 or 261, and ACCTG 211 or 292', 'null', 'null');
