INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 101', 'Business and Enterprise 1', 'Business involves making choices about how to create and capture value through innovation and entrepreneurship, understanding customers, and managing people and resources. The course explores how these choices are shaped by increasing market and societal emphases on corporate social responsibility and sustainability. Introduces the professional skills and capabilities needed in business', 'Restriction: MGMT 101', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 102', 'Business and Enterprise 2', 'Explores and applies fundamental concepts, frameworks, and theories in entrepreneurship, marketing, and management. Emphasises the development of professional skills and capabilities by engaging students in assessing situations, proposing solutions and communicating recommendations.', 'Prerequisite: BUSINESS 101', 'null,sss,test001\r\n', 'null');
INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 103', 'Business and Enterprise 3', 'Explores and applies fundamental concepts, frameworks, and theories in entrepreneurship, marketing, and management. Continues to develop personal skills and professional capabilities by engaging students in a ‘real world’ case that requires them to assess a situation, propose solutions and communicate recommendations.', 'Prerequisite: BUSINESS 101 and at least a Merit average across 16 credits in NCEA Level 3 Business Studies or a B grade or higher in CIE Business Studies', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 151', '', '</p>', 'null', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 151G', 'Communication in a Multicultural Society', 'Communication knowledge and skills are essential in business careers and for interpersonal and intercultural relationships. This course offers a theory-based approach combined with applied communication practices. Communication knowledge, competencies and skills are developed through exploring relationships, mediated communication, writing, team dynamics, oral presentation and technologies.', 'Restriction: BUSINESS 101, 291, MGMT 291', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 200', 'Understanding Business Context', 'Equips students with an appreciation of the forces and actors at work beyond the market. In order to compete in the marketplace firms need to understand their nonmarket context - culture, law, regulations, politics and the physical environment - which all affect business opportunities and strategies. In turn, businesses can influence their environment, both through deliberate nonmarket strategies and as a result of their core operations.', 'Prerequisite: BUSINESS 102 or MGMT 101', 'null', 'null');
