INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 291', 'Communication Processes', 'Employers are demanding business school graduates with strong communication skills. Covers the theory and process of communication in today\'s knowledge and information intensive organisations. Develops oral and written communication skills, including professional presentations. Focuses on the role of interpersonal and team-based communication in building more effective business relationships.', 'Prerequisite: BUSINESS 101 and 102, or BUSINESS 191 and 192, or MGMT 191 and 192, or MGMT 101', 'null', 'null');
