INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 304', 'Strategic Management', 'Examines the processes of formulating and implementing strategies, and the critical thinking behind the multifaceted role of organisations in complex business environments. Focuses on strategy issues in and between a range of commercial and public organisations, from entrepreneurial firms to multinational corporations.', 'Prerequisite: At least 30 points at Stage II and at least 15 points at Stage III in Management, International Business or Innovation and Entrepreneurship', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 307', 'Project Management and Report Writing', 'Develops knowledge and skills in project management and report writing which will underpin BUSINESS 308 Internship and Report.', 'Prerequisite: BUSINESS 309, INNOVENT 201, 303', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 308', 'Internship and Report', 'Develops practical knowledge and hands-on experience through a supervised internship and project in an innovative, entrepreneurial organisation.', 'Prerequisite: BUSINESS 309, INNOVENT 201, 303', 'null', 'null');
