INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 704', 'Quantitative Research Methods', 'Students will become familiar with underlying theory and current best practice in quantitative research through discussion and application of topics including measurement, design (including survey design), and computer-based data analysis.', 'Prerequisite: BUSINESS 710', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Business', 'BUSINESS 705', 'Qualitative Research Methods', 'Students will become familiar with current theory and practice as well as methodological debates in the use of qualitative methodologies, including ethnography, case studies, archival research, participant observation, interview and focus group methods, as well as transcription and analysis. A workshop on coding qualitative data will be included.', 'Prerequisite: BUSINESS 710', 'null', 'null');
