INSERT INTO `businessandeconomics` VALUES ('Information Systems', 'INFOSYS 791B', 'Dissertation - Level 9', '', 'To complete this course students must enrol in INFOSYS 791 A and B, or INFOSYS 791', 'null', 'null');
