INSERT INTO `businessandeconomics` VALUES ('Operations and Supply Chain Management', 'OPSMGT 732', 'Readings in Operations Management', 'A comprehensive review of the research literature in a particular area of operations management. The particular area of research must be jointly agreed upon by the lecturer and student(s) and approved by the Head of Department.\n</p>', 'null', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Operations and Supply Chain Management', 'OPSMGT 752', 'Research Methods – Modelling', 'Mathematical modelling methods in operations management research. Includes simulation techniques, Markov decision models, optimisation methods, game theoretic formulations, and other modelling methods.', 'Prerequisite: INFOMGMT 290 or STATS 255, or equivalent', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Operations and Supply Chain Management', 'OPSMGT 757', 'Project Management', 'Discusses tools and techniques for managing complex projects. Particular focus is given to balancing competing demands among scope, time, cost, and quality. Communication tools for facilitating relationships between the project team and customers are also discussed. Both qualitative and quantitative tools for risk assessment, mitigation, and management are covered.\n</p>', 'null', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Operations and Supply Chain Management', 'OPSMGT 760', 'Advanced Operations Systems', 'A core course in the postgraduate programme in Operations and Supply Chain Management. Provides a deeper understanding of managing internal and external supply chains. Importance of language processing in proactive improvement is emphasised.\n</p>', 'null', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Operations and Supply Chain Management', 'OPSMGT 762', 'Quality Management', 'An investigation of the key concepts and theories of total quality management and its links between systems theory and learning organisations.', 'Prerequisite: STATS 108 or 101 or equivalent', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Operations and Supply Chain Management', 'OPSMGT 766', 'Fundamentals of Supply Chain Coordination', 'Focuses on issues fundamental to supply chain coordination. The impact of information asymmetry, limits of information sharing, incomplete contracts, and other selected topics typically covered in separate subjects such as Contract Theory, Industrial Organisation and Implementation Theory are studied in the supply chain management context. The course will be taught from a quantitative perspective.\n</p>', 'null', 'null', 'null');
INSERT INTO `businessandeconomics` VALUES ('Operations and Supply Chain Management', 'OPSMGT 780', 'Sustainable Transformation', 'Sustainable transformation aspires to balance and integrate societal, economic and environmental dimensions. Focuses on the interrelationships and influences between the sustainability dimensions from a systems dynamics perspective where vision and strategies are translated to sustainable processes, actions, and performance. Explores sustainable transformation of individuals and families through to organisations, supply chains and society as a whole.\n</p>', 'null', 'null', 'null');
