INSERT INTO `businessandeconomics` VALUES ('Tertiary Foundation Certificate Business', 'TFCBUS 92F', 'Foundation Business', 'Develops an understanding of the role of business in a rapidly changing national and international context. Focuses on factors impacting success and value creation including effective management and leadership, understanding customers, innovation and product development, financial and accounting practices, and strategic planning and decision-making.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Academic Integrity', 'ACADINT A01', 'Academic Integrity Course', 'The Academic Integrity Course is an online course designed to increase student knowledge of academic integrity, university rules relating to academic conduct, and the identification and consequences of academic misconduct. Students work through a series of modules, outlining scenarios that they may encounter while studying at university. Each scenario provides information on relevant rules, resources and expected behaviour.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 102', 'Design 1', 'The Conceptual: An introduction, in studio format, to the conceptual realm in which architecture operates, making connections to the cultural, physical, formal, social and political dimensions of architectural design. Emphasises the development of skills and abilities in conceptual thinking and design realisation, using a range of approaches to making and representation.', 'Restriction: ARCHDES 100', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 103', 'Design 2', 'The Formal: An introduction, in studio format, to the discipline of architectural organisation and form-making. Re-examines the traditional notions of typology, precedent, geometry, parti and diagrams. Emphasises strategies that build on and transform understanding for organising form given contemporary programmes and modes of representation.', 'Restriction: ARCHDES 101', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 200', 'Design 3', 'The Domestic: Exploring through design those things both familiar and unfamiliar in our understanding of home, family, privacy, identity, and community. Examines both the most intimate and the most exposed aspects of dwelling. Emphasises the role of precedent in design and addresses scales ranging from the room to the block.', 'Prerequisite: ARCHDES 100 or 102', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 201', 'Design 4', 'The Constructed: An introduction to architectural practice as a complex and collaborative enterprise. Offers the opportunity to explore materials, construction, fabrication processes, and detailing, through making. Requires students to understand the full range of drawings required to move from design concept to actual construction.', 'Prerequisite: ARCHDES 101 or 103', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 300', 'Design 5', 'The Experimental: Students will be exposed to experimental approaches to architectural design that seek to expand the field of architecture. Highlights the role and agency of media in explorative architectural pursuits.', 'Prerequisite: ARCHDES 200', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 301', 'Design 6', 'The Integrated: The culmination of all aspects – conceptual, formal, material, tectonic, environmental, structural – of architectural design within the context of a larger network of infrastructural services. Also requires an understanding of the full range of drawings describing the workings of the building as both an active \'machine\' and place for human comfort.', 'Prerequisite: ARCHDES 200, and 201 or 300', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 302', 'Directed Study', 'A topic approved by the Head of School of Architecture and Planning.', 'Prerequisite: ARCHDES 300 and 301 or Departmental approval', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 700', 'Advanced Design 1', 'A studio based inquiry into an architectural topic approved by the Head of School of Architecture and Planning intended to facilitate in-depth study that is both tailored to a student\'s own interest and aligned with the School\'s research clusters, sharing workshops, discussions, pin-ups and tutorials.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 701', 'Advanced Design 2', 'A studio based inquiry into an architectural topic approved by the Head of School of Architecture and Planning intended to facilitate in-depth study that is both tailored to a student\'s own interest and aligned with the School\'s research clusters, sharing workshops, discussions, presentations and tutorials.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Design', 'ARCHDES 702', 'Adaptive Reuse', 'A studio-based inquiry into an architectural topic in the field of adaptive reuse, approved by the Head of School of Architecture and Planning.', 'Prerequisite: Head of School approval', 'null', 'null');
