INSERT INTO `creativeartsandindustries` VALUES ('Architectural History, Theory and Criticism', 'ARCHHTC 102G', 'Modern Architecture and Urbanism', 'Examines through case studies the cultural contexts that shaped the development of architecture, urban design, landscape and the environment during the twentieth century. Emphasis is placed on the historical developments that influenced changes in style and the theoretical contexts that shaped attitudes towards inhabitation, social organisation, national identity, and cultural self-expression, amongst other things.', 'Restriction: ARCHHTC 100', 'null,yyyyyyyyyyyyyyy,bruce,jane,jane,jane,jane,jane,jane,jane,jane,jane,jane,jane,q,ssssssssssssssssss,2020,2020,2020,2020,2020,2020,2020,2020,2020,2020', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural History, Theory and Criticism', 'ARCHHTC 235', 'Contemporary Architecture and Urbanism', 'Examines late modern, postmodern and contemporary architecture and urbanism. Emphasis is placed on the analysis of buildings, projects and developments that have the potential to inform contemporary architectural design, and on the reading and writing of architectural criticism.', 'Prerequisite: ARCHHTC 102 or 102G, or ARCHHTC 100 and 101', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural History, Theory and Criticism', 'ARCHHTC 236', 'Introduction to Architectural Theory', 'An introduction to architectural and urban theory with emphasis on significant developments in the modern and postmodern periods. Introduction to the contribution of architectural theory to an understanding of the phenomenon of architecture, of architectural design practice and of the problems that architecture and urbanism must solve in the early twenty-first century.', 'Prerequisite: ARCHHTC 102 or 102G, or ARCHHTC 100 and 101', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural History, Theory and Criticism', 'ARCHHTC 237', 'Postmodern and Contemporary Architecture and Urbanism', 'Examines architectural and urban history and theory from the postmodern to the recent and contemporary.', 'Prerequisite: ARCHHTC 102', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural History, Theory and Criticism', 'ARCHHTC 339', 'Premodern Architecture and Urbanism', 'Through case studies from architecture\'s origins to the end of the eighteenth century, this course examines a broad range of cultural landscapes, rural and urban ensembles, architecture and its interiors, ornamental and iconographic programmes, and architectural texts. Distinctions between the conceptual preoccupations, spatial and structural ideas and their use will be drawn for a wide variety of cultural and building traditions.', 'Prerequisite: ARCHHTC 235 and 236, or 202 and 230', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural History, Theory and Criticism', 'ARCHHTC 340', 'Oceanic Architecture and Urbanism', 'Examines the development of architecture and its contexts in Aotearoa New Zealand and the South Pacific, including origins, historical influences, key architects and buildings, identity and changing priorities.', 'Prerequisite: ARCHHTC 235 and 236, or 202 and 230', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural History, Theory and Criticism', 'ARCHHTC 341', 'Worlds of Architecture', 'Examines topics in pre-modern architectural and urban history and theory across the continents of Eurasia, Africa, the Americas, Australia and Oceania.', 'Prerequisite: ARCHHTC 235 and 236, or 237', 'null', 'null');
