INSERT INTO `creativeartsandindustries` VALUES ('Architectural Media', 'ARCHDRC 103', 'Architectural Media 1', 'An introduction to drawing and computing techniques related to design studio practice and an overview of the analytical and critical value of these techniques for design.', 'Restriction: ARCHDRC 102', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Media', 'ARCHDRC 104', 'Architectural Media 2', 'Examines specific types of representation – both freehand and digital – used in architectural media to develop concepts, evaluate architectural thinking and describe and refine design projects.', 'Restriction: ARCHDRC 202', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Media', 'ARCHDRC 202', 'Architectural Media II', 'The study of drawing, computing and related art practices in terms of architectural representation and analysis and the practice of selected techniques.', 'Prerequisite: Both ARCHDRC 100 and 101, or ARCHDRC 102', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Media', 'ARCHDRC 203', 'Architectural Media 3', 'Explores the relationship between methods of architectural drawing and the three-dimensional communication of fabrication and assemblage. Central to this investigation is an understanding of how drawing is evolving in relation to new technologies.', 'Prerequisite: ARCHDRC 102 or 103', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architectural Media', 'ARCHDRC 303', 'Freehand Drawing', 'The examination, through penetrative seeing, of the basic structure, form, tonal colour and textural elements found in the environment and the development of these awarenesses in knowledgeable graphic communications with an emphasis on perceptual expression.', 'Prerequisite: ARCHDRC 201 or 202 or Departmental approval', 'null', 'null');
