INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 732', 'Special Topic: From Public Art to Public Architecture', 'Examines contemporary theory and criticism in public art in order to develop a set of strategies for producing architecture that is more responsive to the ‘public sphere’ in which it is designed, built and continues to exist.', 'Restriction: ARCHGEN 730, 731, 733-735, URBDES 702', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 733', 'Special Topic: Public Urban Space in the Contemporary City', 'Examines the role of public urban space in the city and how history, geography, culture, physical connections and architectural form contribute to its formation. Explores how contemporary cities are transforming their urban environments through design.', 'Restriction: ARCHGEN 730-732, 734-735, URBDES 702', 'null', 'null');
