INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 741', 'Special Topic: Architectural Project Management', 'Examines the theory and practice of managing a building construction project. Explores advanced models of project organisation, procurement, construction contracts, time and cost management and efficient delivery methods.', 'Restriction: ARCHGEN 740, 742-745', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 742', 'Special Topic: Timber Technology', 'Students will build a small building (a shelter) in a group, and then design a second shelter, through concept plans, developed design plans and a scale model.', 'Restriction: ARCHGEN 740, 741, 743-745', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 743', 'Special Topic: Documenting the Intangible', 'Examines how architectural drawing conventions can communicate the intangible, including through drawing at the scale of 1:1.', 'Restriction: ARCHGEN 740-742, 744, 745', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 744', 'Special Topic: Marking Time', 'Explores temporality and how time is expressed in, about and through the medium of spatial practice and representation.', 'Restriction: ARCHGEN 740-743, 745', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 745', 'Special Topic: Tangible Data', 'Explores concepts for new conditions of buildings and cities, in which digital information can be given physical form and physical spatial appearance. Aims to make data ‘touchable’ in space.', 'Restriction: ARCHGEN 740-744', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 750', 'Heritage Processes', 'Examines heritage conservation legislation, policy, guidelines and processes. Includes international context as well as New Zealand laws and processes.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 751', 'Heritage Assessment and Conservation Planning - Level 9', 'Examines the assessment of cultural heritage value and the use and preparation of conservation plans to guide heritage conservation work. Coursework comprises the researching and writing of a conservation plan.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 752', 'Conservation of Materials', 'Examines the theory and practice of conserving materials commonly found in heritage buildings and artefacts, including stone, brick, timber, concrete and steel.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 753', 'Diagnosis and Adaptation', 'Examines the investigation of existing building fabric, diagnosis of issues impacting upon the state of repair or the level of comfort, and the adaptation of heritage buildings, including strengthening, energy upgrading, reuse and the design of additions and alterations.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Architecture General', 'ARCHGEN 754', 'Research Project - Level 9', 'A research project in the field of heritage conservation which may include an internship. Placements and topics to be approved by the Head of School of Architecture and Planning.', 'Prerequisite: ARCHGEN 750, 751', 'null', 'null');
