INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 101G', 'Introduction to Dance and Creative Processes', 'To develop an understanding of our moving bodies through movement awareness, dance improvisation, choreography and creative and analytic writing. Students will undertake both theoretical and practical classes focusing on a range of practices that dancers and movement practitioners use to facilitate kinaesthetic awareness, experimentation, communication and choreography. Students will explore somatic theory and practice, improvisation scores, choreography and dance analysis. DANCE 101 not available for BDanceSt.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 107', 'Dance History and Contexts', 'Study of the historical development in western theatre dance from the nineteenth century to the beginning of the twentieth century.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 110', 'Contemporary Dance and Choreography 1', 'A study of contemporary dance practices through the choreography, creative facilitation and techniques of contemporary dance makers. For BDanceSt students only.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 112', 'Dance Kinesiology', 'Introduction to physiological and kinesiological analysis of dance movements. The study of skeletal alignment, muscular balance and mechanical efficiency.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 120', 'Dance Vocabulary I', 'Introducing the study of diverse dance vocabulary including ballet, contemporary dance and the field of somatics. Students will examine specific technical requirements of identified dance vocabulary.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 121', 'Dance Technique', 'Continuation of work undertaken in DANCE 120 with exploration of skills, repertoire, and merging dance styles.', 'Prerequisite: DANCE 120', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 131', 'Dance Education', 'The study of dance education practice and theory that shapes teaching and learning of dance in school and community contexts. Note: this course does not meet the requirements for teacher registration in New Zealand.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 200', '', '</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 200G', 'Dance and Culture', 'Examines the interrelationship between dance and wider political and cultural movements through practical dance classes and theoretical investigations into diverse cultural environments around the world. Students physically and theoretically engage in the study of various dance forms such as Tango, Salsa, Dabkeh, traditional Chinese dance and Bharata Natyam.', 'Prerequisite: 60 points passed', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 201', 'Dance and Interdisciplinarity', 'Building integrated connections with other arts disciplines such as music, literature, art.', 'Prerequisite: DANCE 101 or 110', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 207', 'Choreography and Performance', 'Focuses on the development and consolidation of choreographic and performance skills.', 'Prerequisite: Any 30 points at Stage I in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 210', 'Contemporary Dance and Choreography 2', 'Study of contemporary choreography practice and theory. Students create choreography that may be shared through film and/or live performances.', 'Prerequisite: DANCE 101 or 110', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 211', 'Special Topic', '', 'Prerequisite: Any 30 points at Stage I in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 212', 'New Zealand Dance Contexts and History', 'Emphasis is on the socio-historical developments of dance in the twentieth century. Choreographers, dancers, designers and composers who have created, influenced and shaped dance in New Zealand will be studied via lectures, videos, scores, and reconstructions.', 'Prerequisite: DANCE 107', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 215', 'Special Topic: Ballet, Hip Hop and Improvisation', '', 'Prerequisite: Any 30 points at Stage I in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 220', 'Dance Vocabulary II', 'Exploring and analysing contemporary dance practices and techniques. Movement skills and performance skills will be developed integrating personal movement with techniques.', 'Prerequisite: DANCE 120', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 222', 'Safe Dance Practices', 'Establishing the theory and practice of safe dance practices within education, performance and health related contexts. Anatomy, kinesiology and dance conditioning methodologies will be studied in relation to dance practice.', 'Prerequisite: DANCE 112', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 231', 'Community Dance', 'Entering diverse community settings and teaching and learning dance; analysing the roles and functions of dance in your own and others\' communities. Note: this course does not meet the requirements for teacher registration in New Zealand.', 'Prerequisite: DANCE 131', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 250', 'Special Topic: Technique: Ballroom and Commercial', '', 'Prerequisite: Any 30 points at Stage I in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 300', 'Dance Project', 'Resident/Guest Artist project that gives students an intensive experience of a particular choreographic vocabulary and repertoire. Not available to BA students.', 'Prerequisite: 30 points at Stage II in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 301', 'Dance and Improvisation', 'Developing an embodied personal practice, and understanding of the theory and practice of contact improvisation and its influence in dance, education and community contexts.', 'Prerequisite: Any 30 points at Stage II in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 302', 'Dance in Aotearoa New Zealand', 'An examination of dance in New Zealand including Mori, Pacific Island, European, and Asian influences. Emphasis will be on developments during the twentieth century of traditional form into contemporary practice, indigenous forms in NZ society, the developments in ballet, contemporary and popular dance.', 'Prerequisite: DANCE 212, or DANCE 200 for students in Transnational Cultures and Creative Practice', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 310', 'Contemporary Dance and Choreography 3', 'The study of contemporary dance practices through the choreography and techniques of contemporary dance makers.', 'Prerequisite: DANCE 210', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 312', 'Dance Production', 'Dance works are choreographed on the students by leading dance professionals in their chosen genre, resulting in a dance production. Not available to BA students.', 'Prerequisite: Any 45 points at Stage II in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 314', 'Dance and Technology', 'Project based study of the interaction between technology and dance. Including hands-on practice with video, digital photography and the study of dance for film.', 'Prerequisite: Any 30 points at Stage II in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 315', 'Dance Composition', 'Studio based course developing improvisational and compositional skills. Choreographic principles are studied as guidelines for structure and design in movement.', 'Prerequisite: Any 30 points at Stage II in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 320', 'Dance Vocabulary III', 'Refining and deepening dance practices and pedagogy strategies specific to contemporary dance. Choreographic research methods are introduced with emphasis upon articulating key questions and processes for problem solving.', 'Prerequisite: Any 30 points at Stage II in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 322', 'Professional Practices', 'Developing skills and knowledge in planning and managing for careers in the diverse dance professions. Students will develop arts management and financial business skills, such as learning to write grant applications, CVs and personal plans that relate to employment and funding issues.', 'Prerequisite: DANCE 222', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 331', 'Dance Education Research', 'Analysis of dance teaching and learning philosophies, issues and theories as they are translated from texts and curriculum into classroom and community practice. This course does not meet the requirements for teacher registration in New Zealand.', 'Prerequisite: DANCE 231', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 350', 'Special Topic: Contemporary Mori and Pacific Dance', '', 'Prerequisite: Any 30 points at Stage II in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 351', 'Special Topic: Dance Technique', '', 'Prerequisite: Any 30 points at Stage II in Dance Studies', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 720', 'Choreography and Performance Research', 'Investigates choreographic practice and dance creation as a location for artistic production and academic research. Students will reflect on their own choreographic and performance practice through studio-based activities, while examining choreographic and performance theory.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 722', 'Dance in Community and Education Research', 'Examines issues and philosophies critical to the development of dance education in formal and informal contexts in New Zealand and internationally. Personal pedagogical practices are reviewed and dominant discourses critiqued.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 724', 'Research Methods and Critical Analysis in Dance Studies', 'Examines diverse qualitative research methods, critical theory and research ethics. Through practical investigations students will source and critically review literature relevant to their personal research directions.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 730', 'Dance Intensive', 'Advanced practice in the physicality and creation of dance.', 'Prerequisite: Departmental approval required', 'null', 'null');
