INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 772', 'Dance Therapy, Theory and Practice I', 'Students will develop their knowledge of dance therapy through theoretical and practical approaches to understanding the conceptual and theoretical foundations that underpin concepts of therapy, well-being, therapist/client relationships and clinical health care systems.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 773', 'Dance Therapy, Theory and Practice II', 'Builds on DANCE 772. Focuses on deepening theoretical and practical understanding of the needs of a variety of client populations in regard to different ages, issues and settings, in individual and group work.', 'Prerequisite: DANCE 772', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 774', 'Psychology in Dance Movement Therapy', 'Focuses on fundamental skills required for professional clinical settings, including counselling and psychological theories and practice including accurate observation and listening techniques, development of the individual and group therapeutic relationship. Key areas covered include abnormal psychology, developmental psychology, group process and advanced counselling skills.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 775', 'Therapeutic Modalities of DMT', 'Practicums in dance therapy contexts are supervised by experienced dance therapists. Students will extend their real world knowledge and develop their experience in observing, reporting and facilitating dance movement therapy.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 776', 'Awareness and Analysis in DMT', 'Anatomy and kinesiology will involve the study of the structures and systems of the body through both somatic and scientific approaches. Movement observation involves developing key diagnostic and reporting tools in dance movement therapy.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 777A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 777B', 'Practicum in Dance Movement Therapy', 'Advanced practicum placements in dance therapy settings are supervised by experienced dance therapists. Students will extend their practical knowledge and develop their experience in observing, reporting and facilitating dance movement therapy at a professional level.', 'Prerequisite: B average or higher in Part I', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Dance Studies', 'DANCE 791', 'Research Project - Level 9', '</p>', 'null', 'null', 'null');
