INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 193B', 'Performance Skills for Singers 1', 'The introduction of vocal practices that help voice students develop and sustain a professional vocal career. This course aims to give vocal students the knowledge and practical experience necessary to develop and maintain vocal health in diverse performing contexts.', 'Prerequisite: Departmental approval', 'null', 'null');
