INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 196B', 'Popular Music Instrumental Performance', 'The development of instrumental technique and interpretative skills through the in-depth study of scales, rhythm, harmony and the relevant musical analyses of set works. In addition, students will develop skills in improvisation, transcription and sight-reading. This course prepares students who major in Popular Music with 1:1 instrumental tuition and group based ensemble classes.', 'Prerequisite: Entrance is by audition. Departmental approval', 'null', 'null');
