INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 274', 'Jazz Theory and Musicianship 2', 'An exploration of more advanced jazz theory and musicianship skills including aural and harmony. Coursework prepares students for the implementation of fundamental written theoretical skills. This course also includes a keyboard tutorial.', 'Prerequisite: MUS 174', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 275', 'Jazz Composition and Arranging 1', 'Composition and arranging in the jazz idiom exploring small ensemble and big band contexts. Scoring, voicing concepts and sectional writing that assist students in the development of a portfolio of work.', 'Prerequisite: MUS 274', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 276', 'Jazz History', 'A critical examination of musical styles, performers, cultural and industrial contexts surrounding jazz musics from the mid-nineteenth century, including ragtime, through New Orleans, swing, be-bop, cool, free, third-stream and post-bop. An in-depth study of primary exponents of various styles. Audio and visual materials are a major component of study.', 'Prerequisite: 30 points passed in Music', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 277', 'Jazz Project 2', 'Participation and development of pertinent skills towards the completion of a collaborative jazz music project.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 280', 'Creative Practice in Popular Music 3', 'Specific exploration and the continued development of ideas and processes in the creation and presentation of popular music through workshops, seminars and group discussion. Students will write songs, complete arrangement exercises, use music recording and production techniques and present aspects of their coursework in live performance.', 'Prerequisite: MUS 181 and 182 or 196', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 281', 'Creative Practice in Popular Music 4', 'Continuation of work undertaken in MUS 280.', 'Prerequisite: MUS 280, 284', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 282', 'Popular Music Vocal Performance', 'The development of vocal technique and interpretative skills through the in-depth study of vocal production techniques pertinent to contemporary popular music vocal performance. The emphasis will be on the development of practices to enhance the performance of original songs written by the students, as well at times techniques necessary to successfully perform songs written by others. This course prepares students who major in Popular Music with 1:1 vocal tuition and group based ensemble classes.', 'Prerequisite: MUS 183 or 196', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 283', 'Popular Music Instrumental Performance Skills 3', 'Continuation of the work undertaken in MUS 183.', 'Prerequisite: MUS 183 or JAZZ 231', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 284', 'Popular Music Theory and Musicianship 1', 'Training in practical musicianship and contemporary music writing skills pertinent to a popular music practitioner. Continued development of aural recognition skills with an emphasis on transcription and sight singing skills.', 'Prerequisite: MUS 104 or 185', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 287', 'Popular Music Theory and Musicianship 2', 'Further training in practical musicianship and contemporary music writing skills pertinent to a popular music practitioner. Continued development of aural recognition skills with an emphasis on transcription and sight singing skills.', 'Prerequisite: MUS 284', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 288', 'Popular Music Analysis', 'Musical analysis in the popular music idiom. Examines techniques of deconstructing music and text from a variety of musical styles in order to identify significant characteristics or trends in composition and lyric writing.', 'Prerequisite: MUS 287', 'null', 'null');
