INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 323', 'Performance Skills 5', 'Further development of a wide range of performance skills beyond those gained in the instrumental/vocal studio, including ensemble techniques, conducting, languages for singers, pedagogy, orchestral audition skills, second instrument study, musicians\' health.', 'Prerequisite: MUS 322', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 324', 'Advanced Studies in Performance Practice', 'Studies in aspects of historical performance practice, using eighteenth century treatises as well as secondary sources. Exploration of topics including rhetoric, gesture, baroque dance, ornamentation and articulation patterns. Study of an historic instrument may be available as an elective within this course.', 'Prerequisite: MUS 224', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 330', 'Music Production 3', 'Instruction in the use of the School of Music\'s professional-level multichannel recording studios supported by practical exercises in popular music production. Topics include: vocal, guitar, and drum recording; synthesis; industry-standard production techniques; and mastering. Coursework will require coordination with performers both within and outside the class.', 'Prerequisite: MUS 219 or 231', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 331', 'Music Production 4', 'A continuation and expansion of the topics addressed in MUS 330 including collaborative projects that concentrate on the production of a popular music \'single\', including the professional and legal issues associated with high-level studio production. Coursework will require coordination with performers both within and outside the class.', 'Prerequisite: MUS 318 or 330', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 332', 'Music Production Project 1', 'An advanced music production and engineering research project, in which an investigation into professional, technological and industrial issues is conducted through practical experience in a professional setting.', 'Prerequisite: MUS 219 or 231', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 333', 'Music Production Project 2', 'An advanced music production and engineering research project, in which an investigation into professional, technological and industrial issues is conducted through practical experience in a professional setting.', 'Prerequisite: MUS 219 or 330', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 334', 'Sound Design for Film and Video Games', 'An exploration of film and video game sound design techniques supported by practical studio-based exercises. Topics include: automatic dialogue replacement (ADR), sound effects recording (Foley), soundscape recording, sound design, three-dimensional sound modeling, computer-generated music, and psychoacoustics.', 'Prerequisite: MUS 211 or 219 or 230', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 340', 'Sound, Style and Syntax', 'A study and in-depth analysis of repertoire from the eighteenth to the twenty-first centuries.', 'Prerequisite: MUS 140 and 201, or 143 and 204', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 343', 'Music in Aotearoa New Zealand', 'A focused profile of the development of music in Aotearoa New Zealand, concentrating on the issue of cultural identity and the contexts of music composition and performance across classical genres, jazz and pop, contemporary and traditional Mori music (including taonga poro), and music from Asia and the Pacific region.', 'Prerequisite: MUS 243 or 30 points at Stage II in Music', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 345', 'History, Music and Ideas: Rethinking the Classical Canon', 'Raises issues specific to classical music in one or more concentrated historical periods. Students will get to know in some detail a designated repertoire of musical works, whilst exploring critical topics such as periodisation, canon formation and reception history.', 'Prerequisite: MUS 140 or 143 or 145, and 30 points from Stage II in Music', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 346', 'Experimental Music in the 20th and 21st Centuries', 'Tracks definitions and developments in ‘experimental’ music since the early twentieth century. Concepts of modernism and postmodernism as related to musical composition, performance and listening are the central focus.', 'Prerequisite: MUS 140 or 143 or 145, and 30 points from Stage II in Music', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 347', 'Genre and Convention in Instrumental Music', 'Explores the complexities of musical style, historicism and aesthetics as related to one or more instrumental genres (such as the symphony, the string-quartet or piano prelude) and related conventions. Close readings of paradigmatic works will be essential.', 'Prerequisite: MUS 140 or 143 or 145, and 30 points from Stage II in Music', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 348', 'Music on Stage and Screen', 'Considers the role of music in one or more of the dramatic arts – opera, musical, ballet, modern dance, film – in any given historical period. Offers opportunity to study specific repertoire in some detail, as well as to investigate music’s contribution to dramatic spectacle, characterisation, narrative and non-narrative structures.', 'Prerequisite: MUS 140 or 143 or 145, and 30 points from Stage II in Music', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 349', 'Topic in World Music', 'An intensive performance-based course that focuses on a specific regional musical tradition.', 'Prerequisite: 30 points at Stage II in Music', 'null', 'null');
