INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 701', 'Advanced Analysis', 'Develops advanced analytical research skills, focusing on one or more specific repertoires and/or analytical techniques (such as voice-leading analysis, schemata, topics, set theory, metrical analysis or form-functional analysis).', 'Prerequisite: MUS 340 or 342 or MUSIC 301', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 707', 'Research and Practice in Conducting', 'The development of advanced conducting skills and techniques in a variety of ensemble situations including instrumental and choral/vocal. Includes research into score preparation and rehearsal skill development.', 'Prerequisite: MUS 306', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 710', 'Composition Research Portfolio', 'Though individually negotiated creative projects and supporting studies in instrumental/vocal composition or sonic arts, students deliver an end-of-semester portfolio of original compositions, wherein skills are refined through targeted research in notational and/or sonic techniques, repertoire study and critical thinking. To take supporting studies in both sonic arts and instrumental composition students should enrol in the corresponding elective MUS 714 or 715.', 'Prerequisite:MUS 311 or 315', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 711', 'Composition Research Project - Level 9', 'A customised creative project in instrumental/vocal composition and/or sonic arts realised through an end-of-semester portfolio of original compositions.', 'Prerequisite: 30 points from MUS 710, 770, 780', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 714', 'Advanced Orchestration', 'Advanced orchestration and instrumentation, including contemporary instrumental and vocal techniques, with practical scoring exercises. Composition students are expected to write some original music in this course.', 'Prerequisite: MUS 314', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 715', 'Advanced Sonic Arts', 'Examination of a wide range of advanced sound-based compositional techniques including multichannel acousmatic music, live sonic arts, algorithmic music, sonic art in the natural environment, visual music and interactive installation.', 'Prerequisite: MUS 315', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 720', 'Classical Performance Research', 'Creative research in aspects of solo performance. Relevant ensemble work, including orchestral rehearsals and performance, may be required.', 'Prerequisite: MUS 321 or 307 or Departmental approval', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 722', 'Advanced Ensemble Performance 1', 'Advanced work in the field of chamber music and ensemble playing.', 'Prerequisite: 15 points from MUS 321, 371, 391, 397', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 723', 'Advanced Ensemble Performance 2', 'Advanced work in the field of chamber music and ensemble playing.', 'Prerequisite: MUS 722', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 724', 'Studio Pedagogy Research and Practice', 'The study of instrumental technique, repertoire, studio pedagogy research, and aspects of pedagogy for studio teaching.', 'Prerequisite: 15 points from MUS 321, 371, 382', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 726', 'Aspects of Performance Practice', 'Selected research for discussion and investigation from the field of Performance Practice and its documentation. The study of source materials; individual projects; performance and/or teaching and direction of music from the area studied.', 'Prerequisite: MUS 224 or 324 or MUSIC 251 or 351 or Departmental approval', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 727', 'Advanced Auxiliary Performance 1', 'Advanced tuition on an approved instrument or voice suitable for a practical component to complement a student’s other music study.', 'Prerequisite: MUS 290 or 390', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 728', 'Advanced Auxiliary Performance 2 - Level 9', 'Further advanced tuition on an approved instrument or voice suitable for a practical component to complement a student’s other music study.', 'Prerequisite: MUS 727', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 729', 'Music Performance Research Project - Level 9', 'A customised performance project incorporating solo performance, conducting, relevant ensemble work, improvisational skills, presentation of original work and the application of advanced instrumental techniques, as appropriate.', 'Prerequisite: 30 points from MUS 707, 720, 770, 780', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 736', 'Creative Studies in Music Technology', 'The development of advanced creative and technological skills leading to computer-based creative practice.', 'Prerequisite: 15 points from MUS 315, 330-334', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 737', 'Music Technology Research', 'Advanced technical, creative, practical and theoretical training in music technology-related disciplines including: general music technology, technology in music education, performance and technology, computer music, musical interface design, music and AI, sound design for games and film, sound recording, live sound and music production.', 'Prerequisite: 15 points from MUS 315, 330-334', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 738', 'Creative Practice Research Project - Level 9', 'A customised creative practice project employing a combination of performance, composition and/or production modes.', 'Prerequisite: 30 points from MUS 710, 720, 737, 770, 780', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 742', 'Research Project in Musicology - Level 9', 'An independent course in musicological research.', 'Prerequisite: MUS 743', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 743', 'Research Methods', 'An overview of concepts and methods for music research including an introduction to quantitative, qualitative, creative practice, educational, indigenous, historical, interdisciplinary and practice-led approaches. Includes ethical and social considerations of research practices. Students use methodological knowledge to develop and present a project proposal for their capstone project.', 'Restriction: MUS 741', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 744', 'Musicians’ Health and Well-being', 'An advanced examination of the critical physical and psychological health issues musicians encounter when preparing for performances and when performing. Topics include aspects of musculoskeletal health, focal dystonia, the protection of the voice and hearing, the role of movement disciplines and the management of stress and music performance anxiety. An inquiry into research from both science and arts disciplines that informs how musicians maintain physical and psychological well-being.', 'Prerequisite: Departmental approval', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 747', 'Research in Musicology', 'An overview of the discipline of musicology, its principal concepts and associated methods of research. Students consider key texts from the scholarly literature and musicological viewpoints and perspectives. This course also develops advanced writing skills.', 'Prerequisite: 15 points from MUS 340, 345-348', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 748', 'Conducting Repertoire and Pedagogy', 'An overview of conducting literature and pedagogical skills for the rehearsal, the concert platform and music education contexts. The course includes analysis, score preparation, practical sessions and requires attendance at designated rehearsals and performances.', 'Prerequisite: MUS 206 or MUSIC 206', 'null', 'null');
