INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 765', 'Music Entrepreneurship', 'An advanced examination of entrepreneurial and business skills for the musician and creative practitioner. Includes the development of specialised technological skills, case studies and innovative approaches to music marketing, arts and cultural engagement, education and music career development.', 'Prerequisite: 30 points at Stage III in Music', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 767', 'Music Education Research and Practice', 'A review of current music education, community music and studio pedagogy research along with the application of practical teaching and workshop techniques and methods relevant for school and community contexts.', 'Prerequisite: MUS 360 or 362 or 363', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 768', 'Community Music Research Project - Level 9', 'Music community and/or pedagogy music research project. Includes fieldwork in music industry, community, school, or studio contexts and a research report.', 'Prerequisite: 30 points from MUS 707, 724, 767', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 770', 'Jazz Performance Research', 'Practical research in instrumental technique leading to the development of advanced improvisational skills. Students prepare a recital reflecting the technical work undertaken in the semester. Students engage with practice through ensemble and 1:1 instruction.', 'Prerequisite: MUS 371 or JAZZ 302', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 772', 'Jazz Composition and Arranging I', 'Jazz arranging and composition for mixed ensembles. Through the analysis and study of advanced compositional and orchestration techniques, students produce original research material for recorded portfolio. Students are encouraged to perform with a \'mentor\' from the jazz faculty in the development of a creative process and individual style.', 'Prerequisite: MUS 376 or JAZZ 306', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 773', 'Jazz Composition and Arranging II', 'A continuation of work undertaken in MUS 772 for a variety of ensembles.', 'Prerequisite: MUS 772', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 774', 'Jazz Collaborative Project', 'Students undertake a research project combining compositional and performance elements from multiple genres: world music, classical, rock, for example, in a blend of contemporary influences. Students contribute original material and written documentation for a recorded portfolio.', 'Prerequisite: MUS 376 or JAZZ 306, and MUS 371 or JAZZ 302', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Music', 'MUS 780', 'Popular Music Research', 'The development of advanced song writing and popular music composition skills. Students engage in an in-depth study of lyric writing, word setting, and compositional elements, compose a significant body of new songs and compositions, and produce a research portfolio of recordings and scores of these works.', 'Prerequisite: MUS 381 or Departmental approval', 'null', 'null');
