INSERT INTO `creativeartsandindustries` VALUES ('Urban Design', 'URBDES 702', 'Urban Design Theory and Practice', 'The language of urban design, urban analysis, urban history, contemporary theory, international and local practice, allied disciplines, cities in the developing world and pacific urbanism.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Design', 'URBDES 703', 'Elective Study', 'Topics approved by the Head of School of Architecture and Planning.\n</p>', 'null', 'null', 'null');
