INSERT INTO `creativeartsandindustries` VALUES ('Urban Design', 'URBDES 710', 'Urban Design Studio 1 - Level 9', 'An urban design project involving in-depth specialised research on the implications for urban design at the strategic scale.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Design', 'URBDES 720', 'Urban Design Studio 2 - Level 9', 'An advanced urban design project involving highly specialised research related to the analysis and design of the built environment.', 'Prerequisite: URBDES 710', 'null', 'null');
