INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 102', 'Urban Planning Economics', 'An introduction to how economic theory, at both the micro and macro levels, effects urban planning policy development and decision making, with reference to how economic development can be integrated into effective urban planning policy formulation.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 103', 'Introduction to Visual Literacy and Research Skills', 'An introduction to visualisation techniques and drawing skills needed to interpret, represent and communicate design ideas for urban planning, and to the research skills and techniques including the use of Geographic Information System (GIS) tools relevant for urban planning.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 104', 'Introduction to Urban Planning Law and Governance', 'An introduction to the key urban planning statutes and the land tenure system; New Zealand society and constitutional framework, including the Treaty of Waitangi, theories and values of democracy relevant for urban planning.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 105', 'Urban Environment Issues', 'An introduction to ecological processes in an urban context.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 110', 'Urban Planning Studio One', 'An introduction to local and site contexts and the relationships between the land use patterns and the scale and type of buildings.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 111', 'Urban Planning Studio Two', 'An introduction to urban design principles and concepts, and the relationships between land use patterns and street networks.\n</p>', 'null', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 201', 'Urban Policy Analysis', 'The application of critical quantitative and qualitative research skills and methods for urban planning.', 'Prerequisite: URBPLAN 101-105, or 30 points passed in Global Environment and Sustainable Development', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 202', 'Urban Planning Implementation and Law', 'A critical understanding of the concepts and principles of relevance to urban planning legislation, practice and decision-making.', 'Prerequisite: URBPLAN 101-105', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 203', 'Urban Infrastructure', 'A critical analysis of infrastructure provision, modelling, and assets management provision.', 'Prerequisite: URBPLAN 101-105, or GEOG 101, 102, 140 or GISCI 140, and URBPLAN 103', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 204', 'Urban Planning Social Theory and Practice', 'A critical analysis of the urban social issues, urban social theory, social justice and deprivation, and gender issues.', 'Prerequisite: URBPLAN 101-105', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 205', 'Transportation Planning', 'A critical analysis of transportation planning, modelling and its relationship with land use activities in the urban environment.', 'Prerequisite: URBPLAN 101-105, or GEOG 101, 102, 140 or GISCI 140, and URBPLAN 103', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 210', 'Urban Planning Studio Three', 'Research and design techniques and skills for evaluating urban design outcomes against urban design criteria at the neighbourhood scale.', 'Prerequisite: URBPLAN 110, 111', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 211', 'Urban Planning Studio Four', 'Examines the complex interrelationships of urban planning issues required to achieve effective and sustainable design solutions at the town/city spatial scale.', 'Prerequisite: URBPLAN 110, 111', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 301', 'Urban Economic Development', 'An evaluation of theories, policies and practices of community and economic development relevant for urban planning.', 'Prerequisite: URBPLAN 201-205, or 30 points at Stage II in Global Environment and Sustainable Development', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 302', 'Heritage/Cultural Issues for Urban Planning', 'A critical analysis of the history, theory and practice of heritage planning in New Zealand and relevant international contexts.', 'Prerequisite: URBPLAN 201-205', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 303', 'Ecology and Resilience', 'A critical analysis of the ecological view towards the concepts of resilience; social-ecological systems models, considering wicked problems and the impacts of climate change.', 'Prerequisite: URBPLAN 201-205', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 304', 'Urban Land Use Economics', 'Examines the principles of urban land economics focusing on economic development, property markets and property development.', 'Prerequisite: URBPLAN 201-205', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 305', 'Mori Urban Planning Issues', 'Mori attitudes, values and aspirations in urban planning with an understanding of the Treaty of Waitangi; post Treaty settlements.', 'Prerequisite: URBPLAN 201-205', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 306', 'Global Contexts and Contemporary Urban Planning Issues', 'Examines how comparative urban planning systems address contemporary urban planning issues in both the New Zealand and international contexts.', 'Prerequisite: URBPLAN 201-205, or 30 points at Stage II in Global Environment and Sustainable Development', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 310', 'Urban Planning Studio Five', 'To develop a critical understanding of regional planning practices, and develop advanced research and designs skills in proposing more sustainable urban form.', 'Prerequisite: URBPLAN 210, 211', 'null', 'null');
INSERT INTO `creativeartsandindustries` VALUES ('Urban Planning', 'URBPLAN 311', 'Urban Planning Studio Six', 'Community engagement, data collection and analysis using a project-based approach.', 'Prerequisite: URBPLAN 210, 211', 'null', 'null');
