INSERT INTO `educationandsocialwork` VALUES ('Education', 'EDUC 784A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education', 'EDUC 784B', 'Research Topic in Education', 'Supervised inquiry in an area of education approved by the Head of the Liberal Arts Programme in the Faculty of Education and Social Work.', 'To complete this course students must enrol in EDUC 784 A and B, or EDUC 784', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education', 'EDUC 787', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education', 'EDUC 787A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education', 'EDUC 787B', 'Researching Mori Education', 'An examination of how best to approach educational research with, by and for Mori. The course is of interest to all social science researchers in Education. Includes the politics and ethics of research involving Mori and other indigenous groups. Particular attention is paid to the development of advanced academic writing skills for research.', 'To complete this course students must enrol in EDUC 787 A and B, or EDUC 787', 'null', 'null');
