INSERT INTO `educationandsocialwork` VALUES ('Education', 'EDUC 791', 'Socio-cultural Examination of Sport and Exercise', 'Critical examination of the cultural meanings and social significance of sport and exercise. Analyses how different sociological approaches have applied key concepts in examining and understanding the importance of sport and exercise practices in contemporary society.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education', 'EDUC 794A', '', '</p>', 'null', 'null', 'null');
