INSERT INTO `educationandsocialwork` VALUES ('Education', 'EDUC 797B', 'Research Portfolio - Level 9', '', 'Prerequisite: A BA(Hons) in Education with at least Second Class Honours, First Division, or equivalent, and an approved research course', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 101', 'Ng Toi: He Whakatakinga', 'Develops students\' knowledge, skills and attitudes associated with planning, teaching and assessing children\'s learning in Ng Toi: dance, drama, music and visual art. Addresses questions such as: Why are Ng Toi important to children\'s learning? How do teachers design quality learning experiences that encourage individual responses from a diverse range of learners? How do we monitor and assess learning?', 'Restriction: EDCURRIC 101', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 102', 'Te Reo Matatini Te Pihinga', 'Develops the knowledge, skills and attitudes associated with planning, teaching and assessing for students\' literacy learning across ng Marautanga Mori. Addresses questions such as: What do teachers need to know to teach literacy effectively? How do teachers\' literacy competencies affect student learning? How do teachers balance the needs of the curriculum and the needs of learners?', 'Restriction: EDCURRIC 102', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 103', 'Te Whaiora', 'Develops understanding of Hauora, philosophies and practices that support learning and teaching within ng Marau. Addresses questions such as: How do teachers implement quality learning experiences based on te akoranga kiri me ng mtauranga hauora for effective learning to occur for a diverse range of learners? How is learning monitored and assessed?', 'Restriction: EDCURRIC 103', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 104', 'Pngarau: He Whakatakinga', 'Develops knowledge and understanding of the nature of Pngarau and tauanga. Considers questions related to primary school Pngarau and tauanga education such as: What is the purpose and role of Pngarau and tauanga in the New Zealand Curriculum Framework? What is meant by thinking mathematically and statistically? What are the components of, and key concepts in the Marautanga Pngarau?', 'Restriction: EDCURRIC 104', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 105', 'Ptaiao: He Whakatakinga', 'Develops an appreciation of the nature of Ptaiao that supports conceptual understandings and quality teaching and learning approaches in Ptaiao education. Addresses questions such as: How do teachers design quality learning environments based on the Marautanga Ptaiao so that positive engagement and effective learning can occur for a diverse range of learners? How is learning monitored and assessed?', 'Restriction: EDCURRIC 105', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 106', 'Tikanga--iwi: He Whakatakinga', 'Develops students\' knowledge and skills associated with planning for teaching and learning in Tikanga  Iwi. Addresses questions such as: What do teachers need to know and understand about the history, nature and purpose of Tikanga  Iwi education? How are curriculum requirements, teaching methodologies, management strategies and resources used to plan for students\' diverse needs? How is learning monitored and assessed?', 'Restriction: EDCURRIC 106', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 107', 'Hangarau: He Whakatakinga', 'Develops knowledge, skills and attitudes associated with planning, teaching and assessing for children\'s learning in the Marautanga Hangarau. Addresses questions such as: What do teachers need to know about the nature and purpose of the Marautanga Hangarau? How do teachers design quality learning experiences for a diverse range of learners? How is learning monitored and assessed?', 'Restriction: EDCURRIC 107', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 108', 'Pngarau: He Whakatakinga', 'Develops knowledge and understanding of the discipline of Pngarau and its relevance and purpose. Understands how the discipline is manifested in the form of curricula documents and guides. Considers questions such as what does it mean for children to know pngarau and the associated debates and related theory. Examines the specific pngarau education discourse in te reo Mori.', 'Restriction: EDCURRM 104', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 109', 'Te reo Matatini 1: Te Pihinga', 'Develops knowledge, skills and attitudes associated with planning, teaching and assessing for learners\' literacy learning across Marautanga. Examines a range of pedagogical practices and beliefs, including second language learning theory and pedagogy to teach literacy effectively and to improve outcomes for learners.', 'Restriction: EDCURRM 101', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 113', 'Hangarau me te Ptaiao - He Whakatakinga', 'Develops knowledge, skills and attitudes associated with planning, teaching and assessing for learners\' literacy learning across Marautanga. Examines a range of pedagogical practices and beliefs, including second language learning theory and pedagogy to teach literacy effectively and to improve outcomes for learners.', 'Restriction: EDCURRM 105', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 117', 'Ng Toi: He Whakatakinga', 'Inquires into the place of Ng Toi in education, and develops capability and understanding through experiences in each of the three Ng Toi disciplines: Toi Ataata; Ng Mahi a te Rhia; and Toi Puoro. Designs for learning by applying pedagogical, curriculum and content knowledge to select approaches and resources for Ng Toi learning experiences for valued outcomes for learners.', 'Restriction: EDCURRM 101', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 119', 'Tikanga -iwi: He Whakatakinga', 'Develops students\' knowledge and skills associated with planning for teaching and learning in tikanga  iwi. Examines the history, nature and purpose of tikanga  iwi education. Develops students’ knowledge of curriculum requirements, social inquiry and resources to plan for students\' diverse needs. Examines how learning is monitored and assessed. Examines the specific tikanga  iwi discourse of te reo Mori.', 'Restriction: EDCURRM 106', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 201', 'Pngarau: He Puwaitanga', 'Develops understanding of pngarau and pedagogical content knowledge for teaching and learning. Designs quality programmes based on knowledge of dispositions, learning and teaching approaches, resources and curricula, and assessment activities. Uses an increasing repertoire of teaching strategies, approaches, learning activities, technologies and assessment for learning. Communicates using the specific pngarau education discourse in te reo Mori.', 'Prerequisite: EDCURRM 108', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 202', 'Te Reo Matatini Te Puanga', 'Deepens the knowledge, skills and attitudes associated with planning, teaching and assessing for individual students\' learning in the Marautanga Reo Mori. Addresses questions such as: What are effective literacy practices for working with individual learners? How are wider concepts of literacy including bilingualism and biliteracy developed? How is learning monitored and assessed?', 'Restriction: EDCURRIC 202', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 203', 'Te Reo Matatini 2: Te Puanga', 'Further develops the knowledge, skills and attitudes associated with planning, teaching and assessing for learners\' language proficiency and literacy learning across te Marautanga Mori. Examines a range of pedagogical practices and beliefs, including second language learning theory and pedagogy to improve outcomes for learners.', 'Prerequisite: EDCURRM 109', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 204', 'Pngarau: Te Whakaako', 'Develops knowledge, skills and understanding for designing quality learning experiences in Pngarau and tauanga for diverse learners. Considers questions related to primary Pngarau and tauanga education such as: What are the mathematical and statistical concepts and learning progressions in Marautanga? What theoretical models of teaching, learning and assessment best inform teachers about the growth of understanding? What constitutes effective teaching practice?', 'Restriction: EDCURRIC 204', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 205', 'Hangarau me te Ptaiao - He Whakawhanaketanga', 'Develops knowledge in the planning, teaching and assessing of children\'s learning in the hangarau and ptaiao curricula as well as knowledge of relevant progressions and support guidelines. Examines how teachers design quality learning environments for positive engagement and effective learning in hangarau and ptaiao for a diverse range of learners.', 'Prerequisite: EDCURRM 113', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 206', 'Hauora', 'Develops understanding of hauora, its whakapapa, philosophies and practices that support learning and teaching. Examines how teachers implement quality learning experiences based on ng akoranga koiri me ng mtauranga hauora to ensure effective learning for a diverse range of learners. Focuses on how learning is monitored and assessed. Examines the specific te reo Mori discourse in hauora education.', 'Restriction: EDCURRM 103', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 220', 'Special Topic: Te Whakarite Mahere Ako m te Tikanga  Iwi', 'Students examine developing tikanga--iwi programmes that are relevant to Mori medium contexts. Students also examine issues relevant to the planning of tikanga--iwi.\n</p>', 'null', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 301', 'Teaching and Te Reo Mori', 'Integrates curriculum content with approaches to planning, teaching and assessing Te Reo Mori up to Year 10. Addresses such questions as: Why is it important to learn Te Reo Mori? What do teachers need to know to teach Te Reo Mori effectively? What strategies, approaches and resources maximise student motivation and language acquisition in Te Reo Mori?', 'Prerequisite: 225 points passed in the BEd(Tchg) Primary Specialisation', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Mori', 'EDCURRM 304', 'T Tangata', 'Critically examines subjective positions from which groups and individuals make sense of the world and act in it. Asks questions such as: What is social and critical literacy? How can critical literacies be used to challenge our subjectivities and assumptions? How do these literacies intersect with underlying notions and philosophies embedded in Kaupapa Mori education initiatives? What are the issues and tensions in applying a Kaupapa Mori philosophy in education?\n</p>', 'null', 'null', 'null');
