INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 607', 'Physical Education Practice', 'Develops the practical pedagogical content knowledge, skills and attitudes associated with planning, teaching and assessing physical education in learning contexts related to teaching Years 9-11. Addresses questions such as: How do teachers plan lessons in, through and about movement? What do teachers need to know to teach in movement-based learning contexts effectively?\n</p>', 'null', 'null', 'null');
