INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 610B', 'Education Outside the Classroom', 'Develops the pedagogical content knowledge, skills and attitudes associated with planning, teaching and assessing EOTC learning contexts related to teaching in a range of outdoor settings. Addresses questions such as: What is EOTC? Why is it important? What pedagogies support safe, effective learning in EOTC? How may ng tikanga Mori influence outdoor education? Requires participation in a camp-based learning experience.', 'To complete this course students must enrol in EDCURSEC 610 A and B, or EDCURSEC 610', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 611', 'Teaching Health Education 1', 'Integrates multidisciplinary-based knowledge and pedagogical content knowledge with developing understanding, skills, attitudes and values associated with teaching in health education across the secondary school. Addresses questions such as: Why is health education important? How are teachers informed in this subject? How is health education taught ethically and effectively for a diverse range of learners?', 'Restriction: EDCURR 648', 'null', 'null');
