INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 612B', 'Teaching Health Education 2', 'Examines further the theories, concepts and research central to the teaching of health education. Addresses questions such as: What do teachers need to know to teach and assess learning in health education? What is authentic health education assessment and how do we determine and monitor success? How does knowledge of curriculum concepts determine learning at senior school levels?', 'Corequisite: EDCURSEC 611 or 687', 'null', 'null');
INSERT INTO `educationandsocialwork` VALUES ('Education Curriculum Secondary Diploma', 'EDCURSEC 613', 'Teaching and Learning Science 1', 'Develops the content knowledge, skills and understanding consistent with relevant curriculum requirements to enable effective teaching and learning approaches in intermediate and secondary science education. Addresses questions such as: How do teachers design quality learning environments that support positive engagement and effective learning for a diverse range of learners? How is achievement determined and monitored?', 'Prerequisite: Departmental approval', 'null', 'null');
